// src/components/AssignmentList.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft, FiCalendar, FiClock, FiFileText, FiUser } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup,
  Badge
} from 'react-bootstrap';

const sortOptions = [
  { label: 'Due Date ↑',  value: 'dueDate:asc' },
  { label: 'Due Date ↓',  value: 'dueDate:desc' },
  { label: 'Title A→Z',   value: 'title:asc' },
  { label: 'Title Z→A',   value: 'title:desc' },
  { label: 'Created ↑',   value: 'createdAt:asc' },
  { label: 'Created ↓',   value: 'createdAt:desc' }
];

export default function AssignmentList() {
  const navigate = useNavigate();
  const { courseId } = useParams();

  // State quản lý
  const [course, setCourse] = useState(null);
  const [assignments, setAssignments] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('dueDate');
  const [order, setOrder] = useState('asc');
  const [isLoading, setIsLoading] = useState(true);

  // Fetch Course detail
  const fetchCourseDetail = useCallback(async () => {
    try {
      const resp = await axios.get(`/api/student/courses/${courseId}`);
      if (resp.data.success) {
        setCourse(resp.data.data);
      }
    } catch (err) {
      console.error('Error fetching course detail:', err);
    }
  }, [courseId]);

  // Fetch Assignments cho course
  const fetchAssignments = useCallback(async () => {
    try {
      setIsLoading(true);
      const resp = await axios.get(`/api/student/assignments/course/${courseId}`);
      if (resp.data.success) {
        setAssignments(resp.data.data);
        setFiltered(resp.data.data);
      }
    } catch (err) {
      console.error('Error fetching assignments:', err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId]);

  // Khi component mount, fetch cả course và assignments
  useEffect(() => {
    fetchCourseDetail();
    fetchAssignments();
  }, [fetchCourseDetail, fetchAssignments]);

  // Lọc + Sắp xếp Assignments
  useEffect(() => {
    let temp = [...assignments];

    // Filter theo searchQuery (tiêu đề)
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, 'i');
      temp = temp.filter((a) => regex.test(a.title));
    }

    // Sort theo sortBy & order
    temp.sort((a, b) => {
      let fieldA = a[sortBy];
      let fieldB = b[sortBy];
      // Nếu là Date, chuyển thành timestamp
      if (sortBy === 'dueDate' || sortBy === 'createdAt') {
        fieldA = new Date(fieldA).getTime();
        fieldB = new Date(fieldB).getTime();
      } else {
        // Chuyển về lowercase để so sánh string
        fieldA = fieldA ? fieldA.toString().toLowerCase() : '';
        fieldB = fieldB ? fieldB.toString().toLowerCase() : '';
      }
      if (fieldA < fieldB) return order === 'asc' ? -1 : 1;
      if (fieldA > fieldB) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFiltered(temp);
  }, [assignments, searchQuery, sortBy, order]);

  // Handler khi chọn sort option
  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  // Handler khi click xem quiz của assignment
  const goToQuizList = (assignmentId) => {
    navigate(`/student/quiz/${assignmentId}`);
  };

  // Helper function để tính trạng thái due date
  const getDueDateStatus = (dueDate) => {
    const now = new Date();
    const due = new Date(dueDate);
    const diffInDays = Math.ceil((due - now) / (1000 * 60 * 60 * 24));
    
    if (diffInDays < 0) {
      return { status: 'overdue', color: 'danger', text: 'Overdue' };
    } else if (diffInDays === 0) {
      return { status: 'today', color: 'warning', text: 'Due Today' };
    } else if (diffInDays <= 3) {
      return { status: 'soon', color: 'warning', text: `${diffInDays} days left` };
    } else {
      return { status: 'normal', color: 'success', text: `${diffInDays} days left` };
    }
  };

  // Helper function để format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.3,
        ease: "easeOut"
      }
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      paddingBottom: '2rem'
    }}>
      <Container className="py-4">
        {/* Back Button */}
        <motion.div
          initial={{ x: -50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.3 }}
        >
          <Button
            variant="outline-light"
            className="mb-4 shadow-sm"
            onClick={() => navigate(-1)}
            style={{
              borderRadius: '25px',
              padding: '8px 20px',
              fontWeight: '500',
              backdropFilter: 'blur(10px)',
              backgroundColor: 'rgba(255, 255, 255, 0.1)'
            }}
          >
            <FiArrowLeft className="me-2" /> Back to Courses
          </Button>
        </motion.div>

        {/* Header Card */}
        <motion.div
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.4 }}
        >
          <Card 
            className="mb-4 shadow-lg border-0"
            style={{
              background: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.8) 100%)',
              backdropFilter: 'blur(10px)',
              borderRadius: '20px'
            }}
          >
            <Card.Body className="p-4">
              <div className="d-flex align-items-center mb-3">
                <div 
                  className="me-3 d-flex align-items-center justify-content-center"
                  style={{
                    width: '60px',
                    height: '60px',
                    borderRadius: '15px',
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    color: 'white'
                  }}
                >
                  <FiFileText size={24} />
                </div>
                <div>
                  <h2 className="mb-1" style={{ color: '#2d3748', fontWeight: '700' }}>
                    {course?.title || 'Course Assignments'}
                  </h2>
                  {course?.instructorName && (
                    <div className="d-flex align-items-center text-muted">
                      <FiUser className="me-2" size={16} />
                      <span>Instructor: {course.instructorName}</span>
                    </div>
                  )}
                </div>
              </div>
              <Badge 
                bg="primary" 
                className="px-3 py-2"
                style={{ 
                  borderRadius: '10px',
                  fontSize: '0.9rem'
                }}
              >
                {filtered.length} Assignment{filtered.length !== 1 ? 's' : ''}
              </Badge>
            </Card.Body>
          </Card>
        </motion.div>

        {/* Search + Sort Controls */}
        <motion.div
          initial={{ y: 30, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.4, delay: 0.2 }}
        >
          <Card 
            className="mb-4 shadow-sm border-0"
            style={{
              background: 'rgba(255,255,255,0.95)',
              backdropFilter: 'blur(10px)',
              borderRadius: '15px'
            }}
          >
            <Card.Body className="p-3">
              <Row className="align-items-center g-3">
                <Col md={8}>
                  <InputGroup>
                    <InputGroup.Text 
                      style={{ 
                        backgroundColor: 'transparent',
                        border: 'none',
                        borderRight: '1px solid #dee2e6'
                      }}
                    >
                      <FiSearch />
                    </InputGroup.Text>
                    <Form.Control
                      placeholder="Search assignments by title..."
                      value={searchQuery}
                      onChange={e => setSearchQuery(e.target.value)}
                      style={{
                        border: 'none',
                        fontSize: '0.95rem',
                        backgroundColor: 'transparent'
                      }}
                    />
                  </InputGroup>
                </Col>
                <Col md={4}>
                  <InputGroup>
                    <Form.Select
                      value={`${sortBy}:${order}`}
                      onChange={e => handleSortChange(e.target.value)}
                      style={{
                        border: 'none',
                        fontSize: '0.95rem',
                        backgroundColor: 'transparent'
                      }}
                    >
                      {sortOptions.map(opt => (
                        <option key={opt.value} value={opt.value}>
                          {opt.label}
                        </option>
                      ))}
                    </Form.Select>
                    <InputGroup.Text 
                      style={{ 
                        backgroundColor: 'transparent',
                        border: 'none',
                        borderLeft: '1px solid #dee2e6'
                      }}
                    >
                      <FiChevronDown />
                    </InputGroup.Text>
                  </InputGroup>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </motion.div>

        {/* Assignment List */}
        {isLoading ? (
          <motion.div 
            className="text-center py-5"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <div 
              className="d-inline-flex align-items-center justify-content-center"
              style={{
                width: '80px',
                height: '80px',
                borderRadius: '50%',
                background: 'rgba(255,255,255,0.9)',
                backdropFilter: 'blur(10px)'
              }}
            >
              <Spinner animation="border" style={{ color: '#667eea' }} />
            </div>
            <p className="mt-3 text-white">Loading assignments...</p>
          </motion.div>
        ) : filtered.length === 0 ? (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.4 }}
          >
            <Card 
              className="text-center shadow-sm border-0"
              style={{
                background: 'rgba(255,255,255,0.9)',
                backdropFilter: 'blur(10px)',
                borderRadius: '15px'
              }}
            >
              <Card.Body className="py-5">
                <FiFileText size={48} className="text-muted mb-3" />
                <h5 className="text-muted">No assignments found</h5>
                <p className="text-muted mb-0">
                  {searchQuery ? 'Try adjusting your search terms.' : 'No assignments available for this course.'}
                </p>
              </Card.Body>
            </Card>
          </motion.div>
        ) : (
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <Row xs={1} sm={2} lg={3} xl={4} className="g-4">
              <AnimatePresence>
                {filtered.map(assignment => {
                  const dueDateStatus = getDueDateStatus(assignment.dueDate);
                  
                  return (
                    <Col key={assignment._id}>
                      <motion.div
                        variants={cardVariants}
                        whileHover={{ 
                          scale: 1.03,
                          boxShadow: "0 20px 40px rgba(0,0,0,0.15)"
                        }}
                        transition={{ 
                          duration: 0.2,
                          ease: "easeOut"
                        }}
                      >
                        <Card 
                          className="h-100 border-0 shadow-sm"
                          style={{
                            background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%)',
                            backdropFilter: 'blur(10px)',
                            borderRadius: '20px',
                            overflow: 'hidden'
                          }}
                        >
                          {/* Status Banner */}
                          <div 
                            style={{
                              background: dueDateStatus.status === 'overdue' 
                                ? 'linear-gradient(135deg, #ff6b6b 0%, #ee5a52 100%)'
                                : dueDateStatus.status === 'today' || dueDateStatus.status === 'soon'
                                ? 'linear-gradient(135deg, #ffd93d 0%, #ff9500 100%)'
                                : 'linear-gradient(135deg, #51cf66 0%, #40c057 100%)',
                              height: '4px'
                            }}
                          />
                          
                          <Card.Body className="p-4 d-flex flex-column">
                            {/* Title */}
                            <h5 
                              className="mb-3" 
                              style={{ 
                                color: '#2d3748', 
                                fontWeight: '600',
                                lineHeight: '1.3'
                              }}
                            >
                              {assignment.title}
                            </h5>
                            
                            {/* Description */}
                            {assignment.description && (
                              <p 
                                className="text-muted mb-3 flex-grow-1"
                                style={{ 
                                  fontSize: '0.9rem',
                                  lineHeight: '1.5'
                                }}
                              >
                                {assignment.description.length > 80
                                  ? `${assignment.description.slice(0, 80)}...`
                                  : assignment.description}
                              </p>
                            )}
                            
                            {/* Footer */}
                            <div className="mt-auto">
                              {/* Due Date Info */}
                              <div className="d-flex align-items-center justify-content-between mb-3">
                                <div className="d-flex align-items-center text-muted">
                                  <FiCalendar className="me-2" size={14} />
                                  <small style={{ fontSize: '0.85rem' }}>
                                    Due: {formatDate(assignment.dueDate)}
                                  </small>
                                </div>
                                <Badge 
                                  bg={dueDateStatus.color}
                                  className="px-2 py-1"
                                  style={{ 
                                    fontSize: '0.75rem',
                                    borderRadius: '8px'
                                  }}
                                >
                                  {dueDateStatus.text}
                                </Badge>
                              </div>
                              
                              {/* Created Date */}
                              <div className="d-flex align-items-center text-muted mb-3">
                                <FiClock className="me-2" size={14} />
                                <small style={{ fontSize: '0.8rem' }}>
                                  Created: {formatDate(assignment.createdAt)}
                                </small>
                              </div>
                              
                              {/* Action Button */}
                              <Button
                                onClick={() => goToQuizList(assignment._id)}
                                className="w-100 border-0"
                                style={{
                                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                  borderRadius: '12px',
                                  padding: '10px',
                                  fontWeight: '500',
                                  fontSize: '0.9rem'
                                }}
                              >
                                View Quiz
                              </Button>
                            </div>
                          </Card.Body>
                        </Card>
                      </motion.div>
                    </Col>
                  );
                })}
              </AnimatePresence>
            </Row>
          </motion.div>
        )}
      </Container>
    </div>
  );
}
