// src/components/StudentSubjects.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown } from 'react-icons/fi';
import axios from 'axios';

const sortOptions = [
  { label: 'Created Date ↑', value: 'createdAt:asc' },
  { label: 'Created Date ↓', value: 'createdAt:desc' },
  { label: 'Name A→Z',       value: 'name:asc' },
  { label: 'Name Z→A',       value: 'name:desc' }
];

export default function StudentSubjects() {
  // Mặc định studentId tạm
  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  
  // Lưu vào state để sử dụng khi fetch
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [order, setOrder] = useState('asc');

  const [enrolledSubjects, setEnrolledSubjects] = useState([]);
  const [otherSubjects, setOtherSubjects] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // 1. Khi component mount, kiểm tra localStorage để lấy user
  useEffect(() => {
    try {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        const userObj = JSON.parse(storedUser);
        if (userObj && userObj._id) {
          setStudentId(userObj._id);
          return;
        }
      }
    } catch (e) {
      console.warn('Error parsing user from localStorage:', e);
    }
    // Nếu không tìm thấy hoặc lỗi parsing, studentId đã mặc định
  }, []);

  // 2. Fetch danh sách môn đã đăng ký
  const fetchEnrolled = useCallback(async () => {
    try {
      if (!studentId) return;
      const resp = await axios.get(`/api/student/subjects/by-student/${studentId}`);
      if (resp.data.success) {
        setEnrolledSubjects(resp.data.data);
      }
    } catch (err) {
      console.error('Error fetching enrolled subjects:', err);
    }
  }, [studentId]);

  // 3. Fetch tất cả môn (hoặc search) và tách thành 2 nhóm
  const fetchAllSubjects = useCallback(async () => {
    try {
      let url = `/api/student/subjects?sortBy=${sortBy}&order=${order}`;
      if (searchQuery.trim()) {
        url = `/api/student/subjects/search?q=${encodeURIComponent(
          searchQuery
        )}&sortBy=${sortBy}&order=${order}`;
      }
      const resp = await axios.get(url);
      if (resp.data.success) {
        const allSubs = resp.data.data;
        // Dựa vào enrolledSubjects để tách
        const enrolledIds = new Set(enrolledSubjects.map((s) => s._id.toString()));
        const others = allSubs.filter((s) => !enrolledIds.has(s._id));
        setOtherSubjects(others);
      }
    } catch (err) {
      console.error('Error fetching all subjects:', err);
    } finally {
      setIsLoading(false);
    }
  }, [searchQuery, sortBy, order, enrolledSubjects]);

  // Khi studentId thay đổi (do lấy từ localStorage), fetch enrolled
  useEffect(() => {
    setIsLoading(true);
    fetchEnrolled();
  }, [fetchEnrolled]);

  // Khi danh sách enrolled, searchQuery, sortBy, order thay đổi → fetch all subjects
  useEffect(() => {
    if (studentId) {
      fetchAllSubjects();
    }
  }, [fetchAllSubjects, studentId]);

  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Search + Sort Bar */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search subjects..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* My Enrolled Subjects */}
      <section className="mb-10">
        <h2 className="text-xl font-semibold text-gray-700 mb-4">
          My Enrolled Subjects
        </h2>
        {isLoading ? (
          <div className="text-gray-500">Loading...</div>
        ) : enrolledSubjects.length === 0 ? (
          <div className="text-gray-500">
            You have not enrolled in any subject yet.
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {enrolledSubjects.map((subj) => (
              <motion.div
                key={subj._id}
                className="bg-blue-100 border-2 border-blue-300 rounded-lg p-4 flex flex-col justify-between"
                whileHover={{ scale: 1.03 }}
                transition={{ duration: 0.2 }}
              >
                <div>
                  <h3 className="text-lg font-medium text-blue-800">{subj.name}</h3>
                  <p className="text-sm text-blue-600 mt-1">
                    Code: {subj.code || '—'}
                  </p>
                </div>
                <button
                  onClick={() => {
                    window.location.href = `/student/courses?subjectId=${subj._id}&enrolled=true`;
                  }}
                  className="mt-4 bg-blue-600 text-white rounded-md px-3 py-1 text-sm hover:bg-blue-700"
                >
                  View Courses
                </button>
              </motion.div>
            ))}
          </div>
        )}
      </section>

      {/* Other Subjects */}
      <section>
        <h2 className="text-xl font-semibold text-gray-700 mb-4">
          Other Subjects
        </h2>
        {isLoading ? (
          <div className="text-gray-500">Loading...</div>
        ) : otherSubjects.length === 0 ? (
          <div className="text-gray-500">No other subjects found.</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {otherSubjects.map((subj) => (
              <motion.div
                key={subj._id}
                className="bg-yellow-100 border-2 border-yellow-300 rounded-lg p-4 flex flex-col justify-between"
                whileHover={{ scale: 1.03 }}
                transition={{ duration: 0.2 }}
              >
                <div>
                  <h3 className="text-lg font-medium text-yellow-800">
                    {subj.name}
                  </h3>
                  <p className="text-sm text-yellow-600 mt-1">
                    Code: {subj.code || '—'}
                  </p>
                </div>
                <button
                  onClick={() => {
                    window.location.href = `/student/courses?subjectId=${subj._id}&enrolled=false`;
                  }}
                  className="mt-4 bg-yellow-600 text-white rounded-md px-3 py-1 text-sm hover:bg-yellow-700"
                >
                  View Courses
                </button>
              </motion.div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
