// src/components/GradeOverview.jsx
import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { FiArrowLeft, FiSearch, FiChevronDown, FiMoreVertical } from "react-icons/fi";
import { motion } from "framer-motion";

const sortOptions = [
  { label: "Grade ↑",      value: "grade:asc" },
  { label: "Grade ↓",      value: "grade:desc" },
  { label: "Student A→Z",  value: "studentName:asc" },
  { label: "Student Z→A",  value: "studentName:desc" },
  { label: "Assignment A→Z", value: "assignmentTitle:asc" },
  { label: "Assignment Z→A", value: "assignmentTitle:desc" }
];

export default function GradeOverview() {
  const history = useNavigate();
  const { courseId } = useParams(); // Route: /student/grades/:courseId

  // State quản lý
  const [submissions, setSubmissions] = useState([]);       // Danh sách gốc
  const [filtered, setFiltered] = useState([]);             // Sau filter + sort
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("grade");
  const [order, setOrder] = useState("desc");
  const [isLoading, setIsLoading] = useState(true);
  const [courseTitle, setCourseTitle] = useState("");       // Tên course để hiển thị
  const [assignments, setAssignments] = useState([]);       // Danh sách assignments để dropdown search

  // 1. Fetch tất cả submissions cho course
  const fetchGrades = useCallback(async () => {
    try {
      setIsLoading(true);
      // Giả sử backend có endpoint: /api/student/submissions/course/:courseId
      const resp = await axios.get(`/api/student/submissions/course/${courseId}`);
      if (resp.data.success) {
        // Mảng submissions, mỗi phần tử có:
        // { _id, assignment: { _id, title }, student: { _id, profile.fullName }, grade: { score }, submittedAt }
        setSubmissions(resp.data.data);
        setFiltered(resp.data.data);

        // Lấy danh sách unique assignment để dropdown tìm kiếm
        const uniqueAssignments = [
          ...new Map(
            resp.data.data.map((s) => [s.assignment._id, s.assignment])
          ).values(),
        ];
        setAssignments(uniqueAssignments);
      }
      // Ngoài ra có endpoint: /api/student/courses/:courseId để lấy tên course
      const courseResp = await axios.get(`/api/student/courses/${courseId}`);
      if (courseResp.data.success) {
        setCourseTitle(courseResp.data.data.title);
      }
    } catch (err) {
      console.error("Error fetching grades:", err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId]);

  useEffect(() => {
    fetchGrades();
  }, [fetchGrades]);

  // 2. Filter + Sort
  useEffect(() => {
    let temp = [...submissions];

    // 2.1. Filter theo searchQuery (tìm theo assignment title)
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, "i");
      temp = temp.filter((s) => regex.test(s.assignment.title));
    }

    // 2.2. Sort
    temp.sort((a, b) => {
      let fieldA, fieldB;

      switch (sortBy) {
        case "grade":
          fieldA = a.grade?.score ?? 0;
          fieldB = b.grade?.score ?? 0;
          break;
        case "studentName":
          fieldA = a.student?.profile?.fullName?.toLowerCase() || "";
          fieldB = b.student?.profile?.fullName?.toLowerCase() || "";
          break;
        case "assignmentTitle":
          fieldA = a.assignment?.title?.toLowerCase() || "";
          fieldB = b.assignment?.title?.toLowerCase() || "";
          break;
        default:
          fieldA = a.grade?.score ?? 0;
          fieldB = b.grade?.score ?? 0;
      }

      if (fieldA < fieldB) return order === "asc" ? -1 : 1;
      if (fieldA > fieldB) return order === "asc" ? 1 : -1;
      return 0;
    });

    setFiltered(temp);
  }, [submissions, searchQuery, sortBy, order]);

  // 3. Handle sort change
  const handleSortChange = (value) => {
    const [field, ord] = value.split(":");
    setSortBy(field);
    setOrder(ord);
  };

  // 4. Điều hướng sang trang phúc khảo
  const goToAppeal = (submissionId) => {
    history.push(`/student/appeal/${submissionId}`);
  };

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back Button */}
      <button
        onClick={() => history.push(`/student/course/${courseId}`)}
        className="flex items-center bg-green-600 text-white rounded-md px-4 py-2 mb-6 hover:bg-green-700"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Course Detail</span>
      </button>

      {/* Header */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Grade Overview: {courseTitle || "Course " + courseId}
        </h1>
      </div>

      {/* Search + Sort */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Dropdown tìm assignment */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <select
            className="flex-1 px-4 py-2 focus:outline-none border-0"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          >
            <option value="">All Assignments</option>
            {assignments.map((a) => (
              <option key={a._id} value={a.title}>
                {a.title}
              </option>
            ))}
          </select>
        </div>
        {/* Sort */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Danh sách submissions */}
      {isLoading ? (
        <div className="text-gray-600">Loading grades...</div>
      ) : filtered.length === 0 ? (
        <div className="text-gray-600">No submissions found.</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filtered.map((s) => (
            <motion.div
              key={s._id}
              className="relative bg-white border border-gray-200 rounded-md p-4 shadow flex flex-col justify-between"
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              {/* Nút 3 chấm ở góc trên phải */}
              <button
                onClick={() => goToAppeal(s._id)}
                className="absolute top-2 right-2 text-gray-600 hover:text-gray-800"
              >
                <FiMoreVertical size={20} />
              </button>

              <div>
                <h3 className="text-lg font-semibold text-gray-800">
                  {s.assignment.title}
                </h3>
                <p className="text-gray-700 text-sm mt-1">
                  Student: {s.student.profile.fullName}
                </p>
                <p className="text-gray-700 text-sm mt-1">
                  Submitted: {new Date(s.submittedAt).toLocaleDateString()}
                </p>
              </div>
              <div className="mt-4">
                {s.grade?.score != null ? (
                  <p className="text-green-700 text-lg font-medium">
                    Score: {s.grade.score}
                  </p>
                ) : (
                  <p className="text-red-600 text-sm">Not graded yet</p>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}
