import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup
} from 'react-bootstrap';

export default function CourseList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const subjectId = params.get('subjectId');

  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId] = useState(
    () => JSON.parse(localStorage.getItem('user') || `{"_id":"${DEFAULT_STUDENT_ID}"}`)._id
  );

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!subjectId) return;
    setIsLoading(true);

    const cancel = axios.CancelToken.source();
    
    // Luôn lấy danh sách enrolled courses trước
    const enrolledReq = axios.get(
      `/api/student/courses/subject/${subjectId}/student/${studentId}`,
      { cancelToken: cancel.token }
    );

    // Kiểm tra xem có phải load ban đầu không (không search, sort mặc định)
    const isDefaultLoad = !searchQuery.trim() && sortBy === 'title' && order === 'asc';
    
    if (isDefaultLoad) {
      // Load ban đầu: chỉ cần enrolled courses và tất cả courses của subject
      const allCoursesReq = axios.get(
        `/api/student/courses/sort`,
        { 
          params: { subjectId, studentId, sortBy: 'title', order: 'asc' },
          cancelToken: cancel.token 
        }
      );

      Promise.all([enrolledReq, allCoursesReq])
        .then(([enrolledRes, allRes]) => {
          if (enrolledRes.data.success && allRes.data.success) {
            // Tạo Set các course ID đã enrolled
            const enrolledSet = new Set(enrolledRes.data.data.map(c => c._id));
            
            // Lọc courses thuộc subject này và gán trạng thái enrolled
            const subjectCourses = allRes.data.data
              .filter(c => c.subjectId.toString() === subjectId)
              .map(c => ({ 
                ...c, 
                enrolled: enrolledSet.has(c._id) 
              }));
            
            setCourses(subjectCourses);
          } else {
            setCourses([]);
          }
        })
        .catch(err => {
          if (!axios.isCancel(err)) {
            console.error('Lỗi khi load courses ban đầu:', err);
          }
        })
        .finally(() => setIsLoading(false));
        
    } else {
      // Có search hoặc sort: cần API search/sort
      const searchSortUrl = searchQuery.trim() 
        ? '/api/student/courses/search' 
        : '/api/student/courses/sort';
      
      const searchSortParams = searchQuery.trim()
        ? { q: searchQuery, subjectId, studentId, sortBy, order }
        : { subjectId, studentId, sortBy, order };
      
      const searchSortReq = axios.get(searchSortUrl, { 
        params: searchSortParams, 
        cancelToken: cancel.token 
      });

      Promise.all([enrolledReq, searchSortReq])
        .then(([enrolledRes, searchRes]) => {
          if (enrolledRes.data.success && searchRes.data.success) {
            // Tạo Set các course ID đã enrolled
            const enrolledSet = new Set(enrolledRes.data.data.map(c => c._id));
            
            // Lọc courses và gán trạng thái enrolled
            const filteredCourses = searchRes.data.data
              .filter(c => c.subjectId.toString() === subjectId)
              .map(c => ({ 
                ...c, 
                enrolled: enrolledSet.has(c._id) 
              }));
            
            setCourses(filteredCourses);
          } else {
            setCourses([]);
          }
        })
        .catch(err => {
          if (!axios.isCancel(err)) {
            console.error('Lỗi khi search/sort courses:', err);
          }
        })
        .finally(() => setIsLoading(false));
    }

    // Cleanup: hủy request khi component unmount
    return () => cancel.cancel();
  }, [subjectId, studentId, searchQuery, sortBy, order]);
  
  // Xử lý đăng ký course
  const handleEnroll = async (id) => {
    try {
      const { data } = await axios.post(
        '/api/student/enrollments/enroll',
        { studentId, courseId: id }
      );
      if (data.success) {
        // Cập nhật trạng thái enrolled trong danh sách
        setCourses(prev =>
          prev.map(c => c._id === id ? { ...c, enrolled: true } : c)
        );
      } else {
        alert('Enrollment failed.');
      }
    } catch (err) {
      console.error('Lỗi khi enroll course:', err);
      alert('Error enrolling');
    }
  };

  // Xử lý thay đổi sắp xếp
  const onSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <Container className="py-4">
      {/* Nút quay lại danh sách subjects */}
      <Button variant="link" onClick={() => navigate('/student/subjects')} className="mb-4 p-0">
        <FiArrowLeft /> Back to Subjects
      </Button>

      {/* Thanh tìm kiếm và sắp xếp */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search courses..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <InputGroup>
            <Form.Select value={`${sortBy}:${order}`} onChange={e => onSortChange(e.target.value)}>
              <option value="title:asc">Title A→Z</option>
              <option value="title:desc">Title Z→A</option>
              <option value="startDate:asc">Start Date ↑</option>
              <option value="startDate:desc">Start Date ↓</option>
              <option value="credits:asc">Credits ↑</option>
              <option value="credits:desc">Credits ↓</option>
            </Form.Select>
            <InputGroup.Text><FiChevronDown /></InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      <h4>Subject Courses</h4>
      {isLoading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
        </div>
      ) : courses.length === 0 ? (
        <p>No courses found.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {courses.map(c => (
            <Col key={c._id}>
              <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.2 }}>
                <Card border={c.enrolled ? 'success' : 'secondary'} className="h-100">
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{c.title}</Card.Title>
                    <Card.Text className="text-muted mb-2">
                      Credits: {c.credits || 'N/A'}
                    </Card.Text>
                    {c.startDate && (
                      <Card.Text className="text-muted small">
                        Start: {new Date(c.startDate).toLocaleDateString()}
                      </Card.Text>
                    )}
                    <div className="mt-auto d-flex justify-content-between">
                      {c.enrolled ? (
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => navigate(`/student/course/${c._id}`)}
                        >
                          Detail
                        </Button>
                      ) : (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleEnroll(c._id)}
                        >
                          Enroll
                        </Button>
                      )}
                    </div>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
