import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { FiArrowLeft } from "react-icons/fi";

export default function AppealForm() {
  const navigate = useNavigate();
  const { submissionId } = useParams(); // Route: /student/appeal/:submissionId

  // State quản lý
  const [submission, setSubmission] = useState(null);
  const [appealContent, setAppealContent] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  // 1. Fetch submission detail (để hiển thị câu hỏi, grade, v.v.)
  const fetchSubmission = useCallback(async () => {
    try {
      setIsLoading(true);
      // Giả sử backend có endpoint: GET /api/student/submissions/:submissionId
      const resp = await axios.get(`/api/student/submissions/${submissionId}`);
      if (resp.data.success) {
        setSubmission(resp.data.data);
      }
    } catch (err) {
      console.error("Error fetching submission:", err);
    } finally {
      setIsLoading(false);
    }
  }, [submissionId]);

  useEffect(() => {
    fetchSubmission();
  }, [fetchSubmission]);

  // 2. Handle appeal submit
  const handleSubmit = async () => {
    if (!appealContent.trim()) {
      alert("Please enter your appeal request.");
      return;
    }
    try {
      setSubmitting(true);
      // Giả dụ endpoint là POST /api/student/appeals
      await axios.post("/api/student/appeals", {
        submissionId,
        content: appealContent.trim(),
      });
      alert("Appeal submitted successfully.");
      // Quay về trang Grade Overview
      if (submission && submission.assignment && submission.assignment.courseId) {
        navigate.push(`/student/grades/${submission.assignment.courseId}`);
      } else {
        navigate.goBack();
      }
    } catch (err) {
      console.error("Error submitting appeal:", err);
      alert("Failed to submit appeal. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <div className="text-gray-600">Loading submission...</div>
      </div>
    );
  }

  if (!submission) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <p className="text-red-600">Submission not found.</p>
        <button
          onClick={() => navigate.goBack()}
          className="mt-4 bg-green-600 text-white rounded-md px-4 py-2 hover:bg-green-700"
        >
          Back
        </button>
      </div>
    );
  }

  const { assignment, student, grade, answers, content } = submission;

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back Button */}
      <button
        onClick={() => navigate.goBack()}
        className="flex items-center bg-green-600 text-white rounded-md px-4 py-2 mb-6 hover:bg-green-700"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Grades</span>
      </button>

      {/* Header */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-2xl font-bold text-gray-900">
          Appeal for Submission
        </h1>
        {assignment && (
          <p className="text-gray-800 mt-1">
            Assignment: {assignment.title}
          </p>
        )}
        {student && student.profile && (
          <p className="text-gray-800 mt-1">
            Student: {student.profile.fullName}
          </p>
        )}
      </div>

      {/* Details Section */}
      <div className="bg-white rounded-md p-6 mb-8 shadow">
        {/* Nếu quiz: hiển thị câu hỏi và đáp án đã chọn */}
        {assignment && assignment.type === "quiz" && (
          <div className="mb-4">
            <h2 className="text-lg font-semibold text-gray-800 mb-2">
              Your Answers:
            </h2>
            {assignment.questions.map((q, idx) => {
              const selected = answers.find(
                (ans) => ans.questionId === q.questionId
              )?.selectedOption;
              return (
                <div key={q.questionId} className="mb-3">
                  <p className="text-gray-700">
                    {`Question ${idx + 1}: ${q.text}`}
                  </p>
                  <p className="text-gray-700 ml-4">
                    {selected
                      ? `Your choice: ${selected}`
                      : "No answer submitted"}
                  </p>
                  <p className="text-gray-700 ml-4 mt-1">
                    {`Correct: ${q.correctOption}`}
                  </p>
                </div>
              );
            })}
          </div>
        )}
        {/* Nếu essay: hiển thị nội dung nộp */}
        {assignment && assignment.type === "essay" && (
          <div className="mb-4">
            <h2 className="text-lg font-semibold text-gray-800 mb-2">
              Your Submission:
            </h2>
            <p className="text-gray-700">{content || "No content submitted"}</p>
          </div>
        )}
        {/* Hiển thị grade */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 mb-2">
            Grade:
          </h2>
          {grade && grade.score != null ? (
            <p className="text-green-700 text-xl font-medium">
              {grade.score}
            </p>
          ) : (
            <p className="text-red-600">Not graded yet</p>
          )}
        </div>
      </div>

      {/* Textarea để viết appeal */}
      <div className="bg-white rounded-md p-6 shadow mb-8">
        <label className="block text-gray-800 font-medium mb-2">
          Appeal Request:
        </label>
        <textarea
          rows={6}
          className="w-full border border-gray-300 rounded-md p-3 focus:outline-none"
          placeholder="Write your appeal here..."
          value={appealContent}
          onChange={(e) => setAppealContent(e.target.value)}
        />
      </div>

      {/* Submit Button */}
      <button
        onClick={handleSubmit}
        disabled={submitting}
        className={`${
          submitting ? "bg-gray-400" : "bg-blue-600 hover:bg-blue-700"
        } text-white rounded-md px-4 py-2`}
      >
        {submitting ? "Submitting..." : "Submit Appeal"}
      </button>
    </div>
  );
}
