import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup
} from 'react-bootstrap';

export default function CourseList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const subjectId = params.get('subjectId');

  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId] = useState(
    () => JSON.parse(localStorage.getItem('user') || `{"_id":"${DEFAULT_STUDENT_ID}"}`)._id
  );

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!subjectId) return;
    setIsLoading(true);

    const cancel = axios.CancelToken.source();
    
    // Luôn cần 2 API calls để phân biệt enrolled/unenrolled
    const enrolledReq = axios.get(
      `/api/student/courses/subject/${subjectId}/student/${studentId}`,
      { cancelToken: cancel.token }
    );

    // Xác định API call thứ 2 dựa trên search/sort
    const isSearching = searchQuery.trim();
    const rawUrl = isSearching ? '/api/student/courses/search' : '/api/student/courses/sort';
    const rawParams = isSearching
      ? { q: searchQuery, subjectId, studentId, sortBy, order }
      : { subjectId, studentId, sortBy, order };
    
    const rawReq = axios.get(rawUrl, { 
      params: rawParams, 
      cancelToken: cancel.token 
    });

    // Xử lý song song cả 2 requests
    Promise.all([enrolledReq, rawReq])
      .then(([enrolledRes, rawRes]) => {
        if (enrolledRes.data.success && rawRes.data.success) {
          // Tạo Set các course đã enrolled
          const enrolledSet = new Set(enrolledRes.data.data.map(c => c._id));
          
          // Lọc và gán trạng thái enrolled cho tất cả courses
          const annotatedCourses = rawRes.data.data
            .filter(c => c.subjectId.toString() === subjectId) // Chỉ lấy courses của subject này
            .map(c => ({ 
              ...c, 
              enrolled: enrolledSet.has(c._id) // Gán trạng thái enrolled
            }));
          
          setCourses(annotatedCourses);
        } else {
          setCourses([]);
        }
      })
      .catch(err => {
        if (!axios.isCancel(err)) {
          console.error('Lỗi khi load courses:', err);
        }
      })
      .finally(() => setIsLoading(false));

    // Cleanup function để cancel request khi component unmount
    return () => cancel.cancel();
  }, [subjectId, studentId, searchQuery, sortBy, order]);
  
  // Hàm xử lý enroll course
  const handleEnroll = async (id) => {
    try {
      const { data } = await axios.post(
        '/api/student/enrollments/enroll',
        { studentId, courseId: id }
      );
      if (data.success) {
        // Cập nhật trạng thái enrolled trong state
        setCourses(prev =>
          prev.map(c => c._id === id ? { ...c, enrolled: true } : c)
        );
      } else {
        alert('Đăng ký thất bại.');
      }
    } catch (err) {
      console.error('Lỗi khi enroll:', err);
      alert('Có lỗi khi đăng ký');
    }
  };

  // Hàm xử lý thay đổi sort
  const onSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <Container className="py-4">
      {/* Nút quay lại */}
      <Button variant="link" onClick={() => navigate('/student/subjects')} className="mb-4 p-0">
        <FiArrowLeft /> Quay lại Subjects
      </Button>

      {/* Search + Sort */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Tìm kiếm courses..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <InputGroup>
            <Form.Select value={`${sortBy}:${order}`} onChange={e => onSortChange(e.target.value)}>
              <option value="title:asc">Tên A→Z</option>
              <option value="title:desc">Tên Z→A</option>
              <option value="startDate:asc">Ngày bắt đầu ↑</option>
              <option value="startDate:desc">Ngày bắt đầu ↓</option>
              <option value="credits:asc">Tín chỉ ↑</option>
              <option value="credits:desc">Tín chỉ ↓</option>
            </Form.Select>
            <InputGroup.Text><FiChevronDown /></InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      <h4>Các Courses trong Subject</h4>
      {isLoading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
          <p className="mt-2">Đang tải courses...</p>
        </div>
      ) : courses.length === 0 ? (
        <p>Không tìm thấy course nào.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {courses.map(c => (
            <Col key={c._id}>
              <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.2 }}>
                <Card border={c.enrolled ? 'success' : 'secondary'} className="h-100">
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{c.title}</Card.Title>
                    <Card.Text className="text-muted mb-2">
                      Tín chỉ: {c.credits || 'N/A'}
                    </Card.Text>
                    {c.startDate && (
                      <Card.Text className="text-muted small">
                        Bắt đầu: {new Date(c.startDate).toLocaleDateString('vi-VN')}
                      </Card.Text>
                    )}
                    <div className="mt-auto d-flex justify-content-between">
                      {c.enrolled ? (
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => navigate(`/student/course/${c._id}`)}
                        >
                          Xem Chi tiết
                        </Button>
                      ) : (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleEnroll(c._id)}
                        >
                          Đăng ký
                        </Button>
                      )}
                    </div>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
