import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft, FiRefreshCw, FiAlertCircle } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup,
  Nav,
  Tab,
  Pagination,
  Alert,
  Badge,
  Toast,
  ToastContainer
} from 'react-bootstrap';

export default function CourseList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const subjectId = params.get('subjectId');

  // Get student ID from localStorage with better error handling
  const [studentId] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user._id || '60a000000000000000000002';
    } catch {
      return '60a000000000000000000002';
    }
  });

  // States
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [allCourses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('enrolled');
  const [enrollingCourses, setEnrollingCourses] = useState(new Set());
  
  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [coursesPerPage] = useState(8);

  // Toast notifications
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastVariant, setToastVariant] = useState('success');

  // Optimized sorting function with more robust handling
  const sortCourses = useCallback((coursesToSort, sortField, sortOrder) => {
    return [...coursesToSort].sort((a, b) => {
      let aValue = a[sortField];
      let bValue = b[sortField];

      // Handle different data types with null safety
      switch (sortField) {
        case 'startDate':
        case 'enrollmentDate':
          aValue = aValue ? new Date(aValue) : new Date(0);
          bValue = bValue ? new Date(bValue) : new Date(0);
          break;
        case 'credits':
          aValue = Number(aValue) || 0;
          bValue = Number(bValue) || 0;
          break;
        case 'title':
        case 'description':
          aValue = String(aValue || '').toLowerCase();
          bValue = String(bValue || '').toLowerCase();
          break;
        default:
          aValue = aValue || '';
          bValue = bValue || '';
      }

      // Compare values
      if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });
  }, []);

  // Enhanced filtering function
  const filterCourses = useCallback((coursesToFilter, query) => {
    if (!query.trim()) return coursesToFilter;
    
    const searchTerm = query.toLowerCase().trim();
    return coursesToFilter.filter(course => 
      course.title?.toLowerCase().includes(searchTerm) ||
      course.description?.toLowerCase().includes(searchTerm) ||
      course.instructor?.toLowerCase().includes(searchTerm) ||
      course.courseCode?.toLowerCase().includes(searchTerm)
    );
  }, []);

  // Memoized processed courses
  const processedCourses = useMemo(() => {
    const filtered = filterCourses(allCourses, searchQuery);
    const sorted = sortCourses(filtered, sortBy, order);
    
    return {
      enrolled: sorted.filter(course => course.enrolled),
      other: sorted.filter(course => !course.enrolled),
      all: sorted
    };
  }, [allCourses, searchQuery, sortBy, order, filterCourses, sortCourses]);

  // Get current courses for pagination
  const getCurrentCourses = useCallback(() => {
    const courses = activeTab === 'enrolled' 
      ? processedCourses.enrolled 
      : processedCourses.other;
    
    const indexOfLastCourse = currentPage * coursesPerPage;
    const indexOfFirstCourse = indexOfLastCourse - coursesPerPage;
    return courses.slice(indexOfFirstCourse, indexOfLastCourse);
  }, [activeTab, processedCourses, currentPage, coursesPerPage]);

  const getTotalPages = useCallback(() => {
    const courses = activeTab === 'enrolled' 
      ? processedCourses.enrolled 
      : processedCourses.other;
    return Math.ceil(courses.length / coursesPerPage);
  }, [activeTab, processedCourses, coursesPerPage]);

  // Show toast notification
  const showNotification = useCallback((message, variant = 'success') => {
    setToastMessage(message);
    setToastVariant(variant);
    setShowToast(true);
  }, []);

  // Optimized data fetching - single API call
  const fetchCourses = useCallback(async () => {
    if (!subjectId) {
      setError('Subject ID is required');
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.get(
        `/api/student/courses/subject/${subjectId}/student/${studentId}`,
        {
          timeout: 10000, // 10 second timeout
          params: {
            // Optional: add query parameters for server-side filtering/sorting
            // This would be useful for large datasets
            // page: 1,
            // limit: 100,
            // includeInactive: false
          }
        }
      );

      if (response.data.success) {
        const courses = response.data.data || [];
        setCourses(courses);
        
        // If no enrolled courses and user is on enrolled tab, switch to other tab
        const enrolledCount = courses.filter(c => c.enrolled).length;
        if (enrolledCount === 0 && activeTab === 'enrolled') {
          setActiveTab('other');
        }
        
        showNotification(`Loaded ${courses.length} courses successfully`);
      } else {
        throw new Error(response.data.message || 'Failed to fetch courses');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || err.message || 'Failed to load courses';
      setError(errorMessage);
      setCourses([]);
      showNotification(errorMessage, 'danger');
      console.error('Error loading courses:', err);
    } finally {
      setIsLoading(false);
    }
  }, [subjectId, studentId, activeTab, showNotification]);

  // Initial data fetch
  useEffect(() => {
    fetchCourses();
  }, [fetchCourses]);

  // Reset pagination when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [activeTab, searchQuery, sortBy, order]);

  // Enhanced enrollment handler with loading state
  const handleEnroll = useCallback(async (courseId, courseTitle) => {
    if (enrollingCourses.has(courseId)) return; // Prevent double enrollment

    setEnrollingCourses(prev => new Set([...prev, courseId]));

    try {
      const response = await axios.post('/api/student/enrollments/enroll', {
        studentId,
        courseId
      });

      if (response.data.success) {
        // Update course enrollment status
        setCourses(prev =>
          prev.map(course => 
            course._id === courseId 
              ? { ...course, enrolled: true, enrollmentDate: new Date().toISOString() }
              : course
          )
        );
        showNotification(`Successfully enrolled in "${courseTitle}"`);
      } else {
        throw new Error(response.data.message || 'Enrollment failed');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || err.message || 'Error enrolling in course';
      showNotification(errorMessage, 'danger');
      console.error('Enrollment error:', err);
    } finally {
      setEnrollingCourses(prev => {
        const newSet = new Set(prev);
        newSet.delete(courseId);
        return newSet;
      });
    }
  }, [studentId, enrollingCourses, showNotification]);

  // Sort change handler
  const handleSortChange = useCallback((value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  }, []);

  // Tab change handler
  const handleTabChange = useCallback((tab) => {
    setActiveTab(tab);
  }, []);

  // Page change handler
  const handlePageChange = useCallback((pageNumber) => {
    setCurrentPage(pageNumber);
    // Smooth scroll to top of course list
    document.querySelector('.course-list-container')?.scrollIntoView({ 
      behavior: 'smooth', 
      block: 'start' 
    });
  }, []);

  // Retry handler
  const handleRetry = useCallback(() => {
    fetchCourses();
  }, [fetchCourses]);

  const currentCourses = getCurrentCourses();
  const totalPages = getTotalPages();
  const currentTabCourses = activeTab === 'enrolled' 
    ? processedCourses.enrolled 
    : processedCourses.other;

  // Render course card
  const renderCourseCard = useCallback((course) => (
    <Col key={course._id}>
      <motion.div 
        whileHover={{ scale: 1.02, y: -4 }} 
        transition={{ duration: 0.2, ease: "easeOut" }}
        style={{ height: '100%' }}
      >
        <Card 
          border={course.enrolled ? 'success' : 'light'} 
          className={`h-100 shadow-sm ${course.enrolled ? 'border-success' : 'border-light'}`}
          style={{ 
            transition: 'all 0.2s ease',
            borderWidth: course.enrolled ? '2px' : '1px'
          }}
        >
          <Card.Body className="d-flex flex-column">
            <div className="d-flex justify-content-between align-items-start mb-2">
              <Card.Title className="mb-1 flex-grow-1" style={{ fontSize: '1.1rem' }}>
                {course.title}
              </Card.Title>
              {course.enrolled && (
                <Badge bg="success" className="ms-2">
                  Enrolled
                </Badge>
              )}
            </div>
            
            {course.courseCode && (
              <Card.Text className="text-muted small mb-1">
                Code: {course.courseCode}
              </Card.Text>
            )}
            
            <Card.Text className="text-muted mb-2">
              Credits: {course.credits || 'N/A'}
            </Card.Text>
            
            {course.instructor && (
              <Card.Text className="text-muted mb-2">
                Instructor: {course.instructor}
              </Card.Text>
            )}
            
            <Card.Text className="text-muted mb-2 flex-grow-1" style={{ fontSize: '0.9rem' }}>
              {course.description?.length > 100 
                ? `${course.description.substring(0, 100)}...`
                : course.description || 'No description available'
              }
            </Card.Text>
            
            {course.startDate && (
              <Card.Text className="text-muted small mb-2">
                Start: {new Date(course.startDate).toLocaleDateString()}
              </Card.Text>
            )}
            
            {course.enrolled && course.enrollmentDate && (
              <Card.Text className="text-success small mb-2">
                Enrolled: {new Date(course.enrollmentDate).toLocaleDateString()}
              </Card.Text>
            )}
            
            <div className="mt-auto">
              {course.enrolled ? (
                <Button
                  variant="outline-success"
                  size="sm"
                  onClick={() => navigate(`/student/course/${course._id}`)}
                  className="w-100"
                >
                  View Details
                </Button>
              ) : (
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => handleEnroll(course._id, course.title)}
                  disabled={enrollingCourses.has(course._id)}
                  className="w-100"
                >
                  {enrollingCourses.has(course._id) ? (
                    <>
                      <Spinner
                        as="span"
                        animation="border"
                        size="sm"
                        role="status"
                        className="me-1"
                      />
                      Enrolling...
                    </>
                  ) : (
                    'Enroll Now'
                  )}
                </Button>
              )}
            </div>
          </Card.Body>
        </Card>
      </motion.div>
    </Col>
  ), [navigate, handleEnroll, enrollingCourses]);

  return (
    <Container className="py-4">
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <Button 
          variant="outline-secondary" 
          onClick={() => navigate('/student/subjects')} 
          className="d-flex align-items-center"
        >
          <FiArrowLeft className="me-2" /> Back to Subjects
        </Button>
        
        <Button
          variant="outline-primary"
          onClick={handleRetry}
          disabled={isLoading}
          className="d-flex align-items-center"
        >
          <FiRefreshCw className={`me-2 ${isLoading ? 'fa-spin' : ''}`} />
          Refresh
        </Button>
      </div>

      {/* Error Alert */}
      {error && (
        <Alert variant="danger" dismissible onClose={() => setError(null)} className="mb-4">
          <FiAlertCircle className="me-2" />
          {error}
        </Alert>
      )}

      {/* Search and Sort Controls */}
      <Row className="align-items-center mb-4">
        <Col lg={8}>
          <InputGroup>
            <InputGroup.Text>
              <FiSearch />
            </InputGroup.Text>
            <Form.Control
              placeholder="Search courses by title, description, instructor, or code..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              disabled={isLoading}
            />
          </InputGroup>
        </Col>
        <Col lg={4}>
          <InputGroup>
            <Form.Select 
              value={`${sortBy}:${order}`} 
              onChange={(e) => handleSortChange(e.target.value)}
              disabled={isLoading}
            >
              <option value="title:asc">Title Aâ†’Z</option>
              <option value="title:desc">Title Zâ†’A</option>
              <option value="startDate:asc">Start Date â†‘</option>
              <option value="startDate:desc">Start Date â†“</option>
              <option value="credits:asc">Credits â†‘</option>
              <option value="credits:desc">Credits â†“</option>
              <option value="enrollmentDate:desc">Recently Enrolled</option>
            </Form.Select>
            <InputGroup.Text>
              <FiChevronDown />
            </InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      {/* Tabs */}
      <Tab.Container activeKey={activeTab} onSelect={handleTabChange}>
        <Nav variant="tabs" className="mb-4">
          <Nav.Item>
            <Nav.Link eventKey="enrolled" disabled={isLoading}>
              My Courses
              <Badge bg="success" className="ms-2">
                {processedCourses.enrolled.length}
              </Badge>
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="other" disabled={isLoading}>
              Available Courses
              <Badge bg="primary" className="ms-2">
                {processedCourses.other.length}
              </Badge>
            </Nav.Link>
          </Nav.Item>
        </Nav>
      </Tab.Container>

      {/* Course List Container */}
      <div className="course-list-container">
        {isLoading ? (
          <div className="text-center py-5">
            <Spinner animation="border" size="lg" />
            <p className="mt-3">Loading courses...</p>
          </div>
        ) : allCourses.length === 0 ? (
          <Alert variant="info" className="text-center">
            <h5>No courses available</h5>
            <p>There are no courses available for this subject yet.</p>
          </Alert>
        ) : currentTabCourses.length === 0 ? (
          <Alert variant="warning" className="text-center">
            <h5>No courses found</h5>
            <p>
              {activeTab === 'enrolled' 
                ? "You haven't enrolled in any courses yet." 
                : searchQuery 
                  ? `No courses match your search "${searchQuery}".`
                  : "No courses available for enrollment."
              }
            </p>
          </Alert>
        ) : (
          <>
            {/* Course Grid */}
            <Row xs={1} sm={1} md={2} lg={3} xl={4} className="g-4 mb-4">
              {currentCourses.map(renderCourseCard)}
            </Row>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="d-flex justify-content-center mb-3">
                <Pagination size="sm">
                  <Pagination.First 
                    onClick={() => handlePageChange(1)}
                    disabled={currentPage === 1}
                  />
                  <Pagination.Prev 
                    onClick={() => handlePageChange(currentPage - 1)}
                    disabled={currentPage === 1}
                  />
                  
                  {[...Array(totalPages)].map((_, index) => {
                    const pageNumber = index + 1;
                    const isNearCurrent = Math.abs(pageNumber - currentPage) <= 2;
                    const isFirstOrLast = pageNumber === 1 || pageNumber === totalPages;
                    
                    if (isNearCurrent || isFirstOrLast) {
                      return (
                        <Pagination.Item
                          key={pageNumber}
                          active={pageNumber === currentPage}
                          onClick={() => handlePageChange(pageNumber)}
                        >
                          {pageNumber}
                        </Pagination.Item>
                      );
                    } else if (pageNumber === currentPage - 3 || pageNumber === currentPage + 3) {
                      return <Pagination.Ellipsis key={`ellipsis-${pageNumber}`} />;
                    }
                    return null;
                  })}
                  
                  <Pagination.Next 
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage === totalPages}
                  />
                  <Pagination.Last 
                    onClick={() => handlePageChange(totalPages)}
                    disabled={currentPage === totalPages}
                  />
                </Pagination>
              </div>
            )}

            {/* Pagination Info */}
            <div className="text-center text-muted">
              Showing {currentCourses.length} of {currentTabCourses.length} courses
              {totalPages > 1 && ` â€¢ Page ${currentPage} of ${totalPages}`}
            </div>
          </>
        )}
      </div>

      {/* Toast Notifications */}
      <ToastContainer position="top-end" className="p-3">
        <Toast 
          show={showToast} 
          onClose={() => setShowToast(false)}
          delay={4000}
          autohide
          bg={toastVariant}
        >
          <Toast.Header>
            <strong className="me-auto">
              {toastVariant === 'success' ? 'Success' : 'Error'}
            </strong>
          </Toast.Header>
          <Toast.Body className={toastVariant === 'success' ? 'text-white' : ''}>
            {toastMessage}
          </Toast.Body>
        </Toast>
      </ToastContainer>
    </Container>
  );
}
