import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup
} from 'react-bootstrap';

export default function CourseList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const subjectId = params.get('subjectId');

  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId] = useState(
    () => JSON.parse(localStorage.getItem('user') || `{"_id":"${DEFAULT_STUDENT_ID}"}`)._id
  );

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Function to sort courses locally
  const sortCourses = (coursesToSort, sortField, sortOrder) => {
    return [...coursesToSort].sort((a, b) => {
      let aValue = a[sortField];
      let bValue = b[sortField];

      // Handle different data types
      if (sortField === 'startDate') {
        aValue = new Date(aValue || 0);
        bValue = new Date(bValue || 0);
      } else if (sortField === 'credits') {
        aValue = Number(aValue) || 0;
        bValue = Number(bValue) || 0;
      } else if (sortField === 'title') {
        aValue = String(aValue || '').toLowerCase();
        bValue = String(bValue || '').toLowerCase();
      }

      // Compare values
      let comparison = 0;
      if (aValue > bValue) {
        comparison = 1;
      } else if (aValue < bValue) {
        comparison = -1;
      }

      return sortOrder === 'desc' ? comparison * -1 : comparison;
    });
  };

  // Function to filter courses based on search query
  const filterCourses = (coursesToFilter, query) => {
    if (!query.trim()) return coursesToFilter;
    
    const searchTerm = query.toLowerCase();
    return coursesToFilter.filter(course => 
      course.title?.toLowerCase().includes(searchTerm) ||
      course.description?.toLowerCase().includes(searchTerm)
    );
  };

  useEffect(() => {
    if (!subjectId) return;
    setIsLoading(true);

    const cancel = axios.CancelToken.source();
    
    // Get enrolled courses
    const enrolledReq = axios.get(
      `/api/student/courses/subject/${subjectId}/student/${studentId}`,
      { cancelToken: cancel.token }
    );

    // Get all courses for the subject
    const allCoursesReq = axios.get(
      `/api/student/courses/subject/${subjectId}`,
      { cancelToken: cancel.token }
    );

    Promise.all([enrolledReq, allCoursesReq])
      .then(([enrolledRes, allCoursesRes]) => {
        if (enrolledRes.data.success && allCoursesRes.data.success) {
          const enrolledSet = new Set(enrolledRes.data.data.map(c => c._id));
          
          // Annotate all courses with enrollment status
          let annotatedCourses = allCoursesRes.data.data.map(course => ({
            ...course,
            enrolled: enrolledSet.has(course._id)
          }));

          // Apply search filter
          annotatedCourses = filterCourses(annotatedCourses, searchQuery);
          
          // Apply sorting
          annotatedCourses = sortCourses(annotatedCourses, sortBy, order);

          setCourses(annotatedCourses);
        } else {
          setCourses([]);
        }
      })
      .catch(err => {
        if (!axios.isCancel(err)) {
          console.error('Error loading courses:', err);
          setCourses([]);
        }
      })
      .finally(() => setIsLoading(false));

    return () => cancel.cancel();
  }, [subjectId, studentId, searchQuery, sortBy, order]);

  const handleEnroll = async (id) => {
    try {
      const { data } = await axios.post(
        '/api/student/enrollments/enroll',
        { studentId, courseId: id }
      );
      if (data.success) {
        setCourses(prev =>
          prev.map(c => c._id === id ? { ...c, enrolled: true } : c)
        );
      } else {
        alert('Enrollment failed.');
      }
    } catch (err) {
      console.error('Enroll error:', err);
      alert('Error enrolling');
    }
  };

  const onSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <Container className="py-4">
      <Button variant="link" onClick={() => navigate('/student/subjects')} className="mb-4 p-0">
        <FiArrowLeft /> Back to Subjects
      </Button>

      {/* Search + Sort */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search courses..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <InputGroup>
            <Form.Select value={`${sortBy}:${order}`} onChange={e => onSortChange(e.target.value)}>
              <option value="title:asc">Title A→Z</option>
              <option value="title:desc">Title Z→A</option>
              <option value="startDate:asc">Start Date ↑</option>
              <option value="startDate:desc">Start Date ↓</option>
              <option value="credits:asc">Credits ↑</option>
              <option value="credits:desc">Credits ↓</option>
            </Form.Select>
            <InputGroup.Text><FiChevronDown /></InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      <h4>Subject Courses</h4>
      {isLoading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
        </div>
      ) : courses.length === 0 ? (
        <p>No courses found.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {courses.map(c => (
            <Col key={c._id}>
              <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.2 }}>
                <Card border={c.enrolled ? 'success' : 'secondary'} className="h-100">
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{c.title}</Card.Title>
                    <Card.Text className="text-muted mb-2">
                      Credits: {c.credits || 'N/A'}
                    </Card.Text>
                    <Card.Text className="text-muted mb-2">
                      Description: {c.description || 'N/A'}
                    </Card.Text>
                    {c.startDate && (
                      <Card.Text className="text-muted small">
                        Start: {new Date(c.startDate).toLocaleDateString()}
                      </Card.Text>
                    )}
                    <div className="mt-auto d-flex justify-content-between">
                      {c.enrolled ? (
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => navigate(`/student/course/${c._id}`)}
                        >
                          Detail
                        </Button>
                      ) : (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleEnroll(c._id)}
                        >
                          Enroll
                        </Button>
                      )}
                    </div>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
