import React, { useState, useEffect, useMemo, useRef, useCallback } from 'react';
import {
  Container, Row, Col, Card, Button,
  Form, InputGroup, Spinner
} from 'react-bootstrap';
import { FiSearch } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';

// sort options
const sortOptions = [
  { label: 'Created Date ↑', value: 'createdAt:asc' },
  { label: 'Created Date ↓', value: 'createdAt:desc' },
  { label: 'Name A→Z',       value: 'name:asc' },
  { label: 'Name Z→A',       value: 'name:desc' }
];

// debounce hook
function useDebounce(value, delay) {
  const [debounced, setDebounced] = useState(value);
  useEffect(() => {
    const handler = setTimeout(() => setDebounced(value), delay);
    return () => clearTimeout(handler);
  }, [value, delay]);
  return debounced;
}

export default function StudentSubjects() {
  const navigate = useNavigate();
  const DEFAULT_ID = '60a000000000000000000002';
  const abortControllerRef = useRef(null);

  const [studentId, setStudentId] = useState(DEFAULT_ID);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [order, setOrder] = useState('asc');
  const [subjects, setSubjects] = useState([]);
  const [enrolledIds, setEnrolledIds] = useState(new Set());
  
  // États de loading plus granulaires
  const [isFirstLoad, setIsFirstLoad] = useState(true);
  const [isUpdating, setIsUpdating] = useState(false);

  const debouncedSearch = useDebounce(searchQuery, 300);

  useEffect(() => {
    try {
      const u = JSON.parse(localStorage.getItem('user') || '{}');
      if (u._id) setStudentId(u._id);
    } catch {}
  }, []);

  // Fonction pour annuler les requêtes en cours
  const cancelPendingRequests = useCallback(() => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    abortControllerRef.current = new AbortController();
    return abortControllerRef.current.signal;
  }, []);

  // Chargement initial optimisé
  useEffect(() => {
    if (!studentId) return;
    
    const loadInitialData = async () => {
      setIsFirstLoad(true);
      const signal = cancelPendingRequests();
      
      try {
        // Charger inscriptions et matières en parallèle
        const [enrolledRes, subjectsRes] = await Promise.all([
          fetch(`/api/student/subjects/by-student/${studentId}`, { signal }),
          fetch(`/api/student/subjects/sort?sortBy=${sortBy}&order=${order}`, { signal })
        ]);

        if (signal.aborted) return;

        const [enrolledData, subjectsData] = await Promise.all([
          enrolledRes.json(),
          subjectsRes.json()
        ]);

        // Mise à jour atomique des deux états
        if (enrolledData.success && subjectsData.success) {
          const newEnrolledIds = new Set(enrolledData.data.map(s => s._id));
          
          // Utiliser une seule mise à jour d'état via callback pour éviter les re-renders
          setEnrolledIds(newEnrolledIds);
          setSubjects(subjectsData.data);
        }
      } catch (err) {
        if (err.name !== 'AbortError') {
          console.error('Error loading initial data:', err);
        }
      } finally {
        setIsFirstLoad(false);
      }
    };

    loadInitialData();
  }, [studentId, sortBy, order, cancelPendingRequests]);

  // Mise à jour lors des changements de recherche/tri (sans toucher aux inscriptions)
  useEffect(() => {
    if (!studentId || isFirstLoad) return;
    
    const updateSubjects = async () => {
      setIsUpdating(true);
      const signal = cancelPendingRequests();
      
      // Délai minimal pour éviter les flashs trop rapides
      const minDelay = new Promise(resolve => setTimeout(resolve, 100));
      
      try {
        let url;
        if (debouncedSearch.trim()) {
          url = `/api/student/subjects/search?q=${encodeURIComponent(debouncedSearch)}&sortBy=${sortBy}&order=${order}`;
        } else {
          url = `/api/student/subjects/sort?sortBy=${sortBy}&order=${order}`;
        }
        
        const [response] = await Promise.all([
          fetch(url, { signal }),
          minDelay // S'assurer d'un délai minimum
        ]);

        if (signal.aborted) return;

        const data = await response.json();
        if (data.success) {
          setSubjects(data.data);
        }
      } catch (err) {
        if (err.name !== 'AbortError') {
          console.error('Error updating subjects:', err);
        }
      } finally {
        setIsUpdating(false);
      }
    };
    
    updateSubjects();
  }, [studentId, debouncedSearch, sortBy, order, isFirstLoad, cancelPendingRequests]);

  // Nettoyage des requêtes en cours
  useEffect(() => {
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  // Mémoisation optimisée avec dépendances stables
  const { enrolled, others } = useMemo(() => {
    const enrolledList = subjects.filter(s => enrolledIds.has(s._id));
    const othersList = subjects.filter(s => !enrolledIds.has(s._id));
    return { enrolled: enrolledList, others: othersList };
  }, [subjects, enrolledIds]);

  const handleSort = useCallback((e) => {
    const [f, o] = e.target.value.split(':');
    setSortBy(f);
    setOrder(o);
  }, []);

  const goCourses = useCallback((id, flag) => {
    navigate(`/student/courses?subjectId=${id}&enrolled=${flag}`);
  }, [navigate]);

  const renderCards = useCallback((list, variant, btnVariant, flag) => (
    <Row xs={1} sm={2} md={3} lg={4} className="g-3">
      {list.map(sub => (
        <Col key={sub._id}>
          <Card className={`h-100 border-${variant}`}>
            <Card.Body className="d-flex flex-column">
              <Card.Title>{sub.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                Code: {sub.code || '—'}
              </Card.Subtitle>
              <Button
                variant={btnVariant}
                className="mt-auto text-white"
                onClick={() => goCourses(sub._id, flag)}
              >View Courses</Button>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  ), [goCourses]);

  // Loading initial avec animation plus douce
  if (isFirstLoad) {
    return (
      <Container className="py-4">
        <div className="text-center" style={{ minHeight: '200px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div>
            <Spinner animation="border" className="me-2" />
            <span>Loading your subjects...</span>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Search & Sort avec transition plus fluide */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search subjects..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
              style={{ 
                opacity: isUpdating ? 0.7 : 1,
                transition: 'opacity 0.2s ease'
              }}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <Form.Select 
            value={`${sortBy}:${order}`} 
            onChange={handleSort}
            style={{ 
              opacity: isUpdating ? 0.7 : 1,
              transition: 'opacity 0.2s ease'
            }}
          >
            {sortOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </Form.Select>
        </Col>
      </Row>

      <div style={{ minHeight: '20px', marginBottom: '1rem' }}>
        {isUpdating && (
          <div className="text-center">
            <Spinner animation="border" size="sm" className="me-2" />
            <small className="text-muted">Updating...</small>
          </div>
        )}
      </div>

      <div style={{ minHeight: '100px' }}>
        <h4>My Enrolled Subjects</h4>
        {enrolled.length === 0 ? (
          <p>You have not enrolled in any subject yet.</p>
        ) : (
          renderCards(enrolled, 'primary', 'primary', true)
        )}
      </div>

      <div style={{ minHeight: '100px' }} className="mt-4">
        <h4>Other Subjects</h4>
        {others.length === 0 ? (
          <p>No other subjects found.</p>
        ) : (
          renderCards(others, 'warning', 'warning', false)
        )}
      </div>
    </Container>
  );
}
