import React, { useState, useEffect, useMemo } from 'react';
import {
  Container, Row, Col, Card, Button,
  Form, InputGroup, Spinner
} from 'react-bootstrap';
import { FiSearch } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';

// sort options
const sortOptions = [
  { label: 'Created Date ↑', value: 'createdAt:asc' },
  { label: 'Created Date ↓', value: 'createdAt:desc' },
  { label: 'Name A→Z',       value: 'name:asc' },
  { label: 'Name Z→A',       value: 'name:desc' }
];

// debounce hook
function useDebounce(value, delay) {
  const [debounced, setDebounced] = useState(value);
  useEffect(() => {
    const handler = setTimeout(() => setDebounced(value), delay);
    return () => clearTimeout(handler);
  }, [value, delay]);
  return debounced;
}

export default function StudentSubjects() {
  const navigate = useNavigate();
  const DEFAULT_ID = '60a000000000000000000002';

  const [studentId, setStudentId] = useState(DEFAULT_ID);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [order, setOrder] = useState('asc');

  // Raw subjects from server (approved or search sorted)
  const [subjectsRaw, setSubjectsRaw] = useState([]);
  // Enrolled subject IDs
  const [enrolledIds, setEnrolledIds] = useState(new Set());

  // Loading flags
  const [loadingEnrolled, setLoadingEnrolled] = useState(true);
  const [loadingRaw, setLoadingRaw] = useState(true);

  const debouncedSearch = useDebounce(searchQuery, 300);

  // load studentId
  useEffect(() => {
    try {
      const u = JSON.parse(localStorage.getItem('user') || '{}');
      if (u._id) setStudentId(u._id);
    } catch {}
  }, []);

  // fetch enrolled once
  useEffect(() => {
    if (!studentId) return;
    setLoadingEnrolled(true);
    fetch(`/api/student/subjects/by-student/${studentId}`)
      .then(res => res.json())
      .then(body => {
        if (body.success) setEnrolledIds(new Set(body.data.map(s => s._id)));
      })
      .catch(err => console.error(err))
      .finally(() => setLoadingEnrolled(false));
  }, [studentId]);

  // fetch raw subjects on search/sort change (no dependency on enrolled)
  useEffect(() => {
    setLoadingRaw(true);
    const q = debouncedSearch.trim();
    const url = q
      ? `/api/student/subjects/search?q=${encodeURIComponent(q)}&sortBy=${sortBy}&order=${order}`
      : `/api/student/subjects?sortBy=${sortBy}&order=${order}`;

    fetch(url)
      .then(res => res.json())
      .then(body => {
        if (body.success) setSubjectsRaw(body.data);
      })
      .catch(err => console.error(err))
      .finally(() => setLoadingRaw(false));
  }, [debouncedSearch, sortBy, order]);

  // derive enrolled and other lists via memo
  const enrolled = useMemo(
    () => subjectsRaw.filter(s => enrolledIds.has(s._id)),
    [subjectsRaw, enrolledIds]
  );
  const others = useMemo(
    () => subjectsRaw.filter(s => !enrolledIds.has(s._id)),
    [subjectsRaw, enrolledIds]
  );

  const handleSort = e => {
    const [f, o] = e.target.value.split(':');
    setSortBy(f);
    setOrder(o);
  };

  const goCourses = (id, flag) => {
    navigate(`/student/courses?subjectId=${id}&enrolled=${flag}`);
  };

  const renderCards = (list, variant, btnVariant, flag) => (
    <Row xs={1} sm={2} md={3} lg={4} className="g-3 mb-4">
      {list.map(sub => (
        <Col key={sub._id}>
          <Card className={`h-100 border-${variant}`}>
            <Card.Body className="d-flex flex-column">
              <Card.Title>{sub.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">Code: {sub.code || '—'}</Card.Subtitle>
              <Button
                variant={btnVariant}
                className="mt-auto text-white"
                onClick={() => goCourses(sub._id, flag)}
              >View Courses</Button>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  );

  return (
    <Container className="py-4">
      {/* Search & Sort */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search subjects..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <Form.Select value={`${sortBy}:${order}`} onChange={handleSort}>
            {sortOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </Form.Select>
        </Col>
      </Row>

      {/* Enrolled */}
      <h4>My Enrolled Subjects</h4>
      {loadingEnrolled ? <Spinner animation="border" />
        : (enrolled.length === 0
            ? <p>You have not enrolled in any subject yet.</p>
            : renderCards(enrolled, 'primary', 'primary', true)
          )
      }

      {/* Others */}
      <h4 className="mt-4">Other Subjects</h4>
      {loadingRaw ? <Spinner animation="border" />
        : (others.length === 0
            ? <p>No other subjects found.</p>
            : renderCards(others, 'warning', 'warning', false)
          )
      }
    </Container>
  );
}
