// src/components/QuizList.jsx
import React, { useState, useEffect, useCallback } from "react";
import { useHistory, useParams } from "react-router-dom";
import axios from "axios";
import { motion } from "framer-motion";
import { FiArrowLeft } from "react-icons/fi";

export default function QuizList() {
  const history = useHistory();
  const { assignmentId } = useParams(); // Route: /student/quiz/:assignmentId

  // 1. State
  const [assignment, setAssignment] = useState(null);
  const [submission, setSubmission] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [answers, setAnswers] = useState({}); // { questionId: selectedOption }
  const [essayContent, setEssayContent] = useState("");
  const [canEdit, setCanEdit] = useState(false); // còn hạn nộp
  const [hasSubmitted, setHasSubmitted] = useState(false);

  // Lấy studentId từ localStorage hoặc mặc định
  const DEFAULT_STUDENT_ID = "60a000000000000000000002";
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);
  useEffect(() => {
    try {
      const stored = localStorage.getItem("user");
      if (stored) {
        const u = JSON.parse(stored);
        if (u && u._id) setStudentId(u._id);
      }
    } catch (e) {
      console.warn("localStorage user parse error:", e);
    }
  }, []);

  // 2. Fetch assignment detail
  const fetchAssignment = useCallback(async () => {
    try {
      const resp = await axios.get(`/api/student/assignments/${assignmentId}`);
      if (resp.data.success) {
        setAssignment(resp.data.data);
      }
    } catch (err) {
      console.error("Error fetching assignment:", err);
    }
  }, [assignmentId]);

  // 3. Fetch submission (if có) for this student
  const fetchSubmission = useCallback(async () => {
    try {
      const resp = await axios.get(
        `/api/student/submissions/assignment/${assignmentId}`
      );
      if (resp.data.success) {
        // Lọc ra submission đúng studentId
        const allSubs = resp.data.data;
        const mySub = allSubs.find(
          (s) => s.studentId.toString() === studentId
        );
        if (mySub) {
          setSubmission(mySub);
          setHasSubmitted(true);
          // Nếu quiz:
          if (assignment?.type === "quiz" && mySub.answers) {
            const ansMap = {};
            mySub.answers.forEach((ans) => {
              ansMap[ans.questionId] = ans.selectedOption;
            });
            setAnswers(ansMap);
          }
          // Nếu essay:
          if (assignment?.type === "essay" && mySub.content) {
            setEssayContent(mySub.content);
          }
        }
      }
    } catch (err) {
      console.error("Error fetching submission:", err);
    }
  }, [assignmentId, studentId, assignment]);

  // 4. Kiểm tra hạn nộp
  const checkCanEdit = useCallback(() => {
    if (!assignment) return;
    const now = new Date();
    const due = new Date(assignment.dueDate);
    // Nếu chưa quá hạn -> có thể nộp hoặc resubmit
    setCanEdit(now <= due);
  }, [assignment]);

  // 5. Kết hợp fetch và kiểm tra
  useEffect(() => {
    async function loadData() {
      setIsLoading(true);
      await fetchAssignment();
      setIsLoading(false);
    }
    loadData();
  }, [fetchAssignment]);

  useEffect(() => {
    if (assignment) {
      checkCanEdit();
      fetchSubmission();
    }
  }, [assignment, fetchSubmission, checkCanEdit]);

  // 6. Handle chọn đáp án quiz
  const handleOptionChange = (questionId, optionKey) => {
    if (!canEdit) return;
    setAnswers((prev) => ({
      ...prev,
      [questionId]: optionKey,
    }));
  };

  // 7. Handle submit/resubmit
  const handleSubmit = async () => {
    if (!assignment) return;

    // Kiểm tra deadline một lần nữa
    const now = new Date();
    const due = new Date(assignment.dueDate);
    if (now > due) {
      alert("Deadline has passed. Cannot submit.");
      return;
    }

    // Chuẩn bị payload
    let payload = {
      studentId,
      assignmentId,
    };
    if (assignment.type === "quiz") {
      // Chuyển answers thành mảng
      const ansArr = Object.entries(answers).map(([qId, sel]) => ({
        questionId: qId,
        selectedOption: sel,
      }));
      payload.answers = ansArr;
    } else {
      // essay
      payload.content = essayContent;
    }

    try {
      await axios.post("/api/student/submissions/submit", payload);
      alert("Submission successful!");
      // Refresh lại submission
      fetchSubmission();
    } catch (err) {
      console.error("Submit error:", err);
      alert("Failed to submit.");
    }
  };

  if (isLoading || !assignment) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <div className="text-gray-600">Loading...</div>
      </div>
    );
  }

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back to Assignments */}
      <button
        onClick={() => history.goBack()}
        className="flex items-center bg-green-600 text-white rounded-md px-4 py-2 mb-6 hover:bg-green-700"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Assignments</span>
      </button>

      {/* Header Assignment */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          {assignment.title}
        </h1>
        {assignment.courseId && (
          <p className="text-gray-800 mt-1">
            Course ID: {assignment.courseId}
          </p>
        )}
        <p className="text-gray-700 mt-1">
          Due Date: {new Date(assignment.dueDate).toLocaleString()}
        </p>
      </div>

      {/* Nếu quiz */}
      {assignment.type === "quiz" ? (
        <div className="space-y-6 mb-8">
          {assignment.questions.map((q, idx) => (
            <div
              key={q.questionId}
              className="bg-red-100 rounded-md p-4 shadow"
            >
              <h3 className="text-lg font-medium text-gray-800">
                {`Câu ${idx + 1}: ${q.text}`}
              </h3>
              {/* Lặp qua options, 2 ô mỗi hàng */}
              <div className="mt-3">
                {/*
                  Chia thành các cặp: 
                  lấy array [opt0, opt1], [opt2, opt3], ...
                */}
                {Array.from({ length: Math.ceil(q.options.length / 2) }).map(
                  (_, rowIndex) => {
                    const leftIndex = rowIndex * 2;
                    const rightIndex = leftIndex + 1;
                    const leftOption = q.options[leftIndex];
                    const rightOption =
                      q.options[rightIndex] || null;
                    return (
                      <div
                        key={rowIndex}
                        className="flex space-x-4 mt-2"
                      >
                        {/* Left option */}
                        {leftOption && (
                          <label className="flex items-center bg-white border border-gray-300 rounded-md px-3 py-2 flex-1 cursor-pointer hover:bg-gray-50">
                            <input
                              type="radio"
                              name={`q_${q.questionId}`}
                              value={leftOption.key}
                              checked={
                                answers[q.questionId] === leftOption.key
                              }
                              onChange={() =>
                                handleOptionChange(
                                  q.questionId,
                                  leftOption.key
                                )
                              }
                              disabled={!canEdit}
                              className="mr-2"
                            />
                            <span className="text-gray-700">
                              {leftOption.key}. {leftOption.text}
                            </span>
                          </label>
                        )}
                        {/* Right option */}
                        {rightOption && (
                          <label className="flex items-center bg-white border border-gray-300 rounded-md px-3 py-2 flex-1 cursor-pointer hover:bg-gray-50">
                            <input
                              type="radio"
                              name={`q_${q.questionId}`}
                              value={rightOption.key}
                              checked={
                                answers[q.questionId] === rightOption.key
                              }
                              onChange={() =>
                                handleOptionChange(
                                  q.questionId,
                                  rightOption.key
                                )
                              }
                              disabled={!canEdit}
                              className="mr-2"
                            />
                            <span className="text-gray-700">
                              {rightOption.key}. {rightOption.text}
                            </span>
                          </label>
                        )}
                      </div>
                    );
                  }
                )}
              </div>
              {/* Nếu đã graded, hiển thị điểm và đáp án đúng */}
              {submission && submission.grade?.score != null && (
                <p className="text-green-700 mt-2">
                  Your Score: {submission.grade.score}
                </p>
              )}
            </div>
          ))}
        </div>
      ) : (
        // Nếu essay
        <div className="mb-8">
          <label className="block text-gray-800 font-medium mb-2">
            Your Answer:
          </label>
          <textarea
            rows={8}
            className="w-full border border-gray-300 rounded-md p-3 focus:outline-none"
            value={essayContent}
            onChange={(e) => setEssayContent(e.target.value)}
            disabled={!canEdit}
          />
          {/* Hiển thị điểm nếu đã graded */}
          {submission && submission.grade?.score != null && (
            <p className="text-green-700 mt-2">
              Your Score: {submission.grade.score}
            </p>
          )}
        </div>
      )}

      {/* Submit / Resubmit Button */}
      {canEdit ? (
        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white rounded-md px-4 py-2 hover:bg-blue-700"
        >
          {hasSubmitted ? "Resubmit" : "Submit"}
        </button>
      ) : (
        <p className="text-red-600 font-medium">
          The deadline has passed. You can no longer submit.
        </p>
      )}
    </div>
  );
}
