// src/components/CourseList.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

const sortOptions = [
  { label: 'Title A→Z', value: 'title:asc' },
  { label: 'Title Z→A', value: 'title:desc' },
  { label: 'Start Date ↑', value: 'startDate:asc' },
  { label: 'Start Date ↓', value: 'startDate:desc' },
  { label: 'Credits ↑', value: 'credits:asc' },
  { label: 'Credits ↓', value: 'credits:desc' }
];

export default function CourseList() {
  const navigate = useNavigate();
  const location = useLocation();

  // 1. Lấy subjectId từ query param: ?subjectId=xxx
  const params = new URLSearchParams(location.search);
  const subjectId = params.get('subjectId');
  const enrolledParam = params.get('enrolled') === 'true';

  // 2. Lấy studentId từ localStorage, nếu không tồn tại dùng mặc định
  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);

  // 3. State local
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // 4. Khi component mount: lấy studentId từ localStorage
  useEffect(() => {
    try {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        const userObj = JSON.parse(storedUser);
        if (userObj && userObj._id) {
          setStudentId(userObj._id);
          return;
        }
      }
    } catch (e) {
      console.warn('Error parsing user from localStorage:', e);
    }
  }, []);

  // 5. Hàm fetch courses (theo subjectId, studentId, search + sort)
  const fetchCourses = useCallback(async () => {
    if (!subjectId || !studentId) return;
    setIsLoading(true);
    try {
      // Cấu trúc URL: /api/student/courses/subject/:subjectId/student/:studentId
      // Kèm query ?search=&sortBy=&order=
      let url = `/api/student/courses/subject/${subjectId}/student/${studentId}?sortBy=${sortBy}&order=${order}`;
      if (searchQuery.trim()) {
        // Nếu có search, gọi endpoint tìm kiếm (kiểm tra backend có route search theo subject chưa? 
        // Nếu không, filter trên client.)
        url += `&search=${encodeURIComponent(searchQuery)}`;
      }

      const resp = await axios.get(url);
      if (resp.data.success) {
        let data = resp.data.data; // mảng { ...course, enrolled: true|false }

        // Nếu backend chưa hỗ trợ `search` riêng, bạn có thể filter trên client:
        if (searchQuery.trim()) {
          const regex = new RegExp(searchQuery, 'i');
          data = data.filter(
            (c) =>
              regex.test(c.title) || (c.description && regex.test(c.description))
          );
        }

        setCourses(data);
      }
    } catch (err) {
      console.error('Error fetching courses:', err);
    } finally {
      setIsLoading(false);
    }
  }, [subjectId, studentId, searchQuery, sortBy, order]);

  // 6. Effect để fetch khi những giá trị thay đổi
  useEffect(() => {
    fetchCourses();
  }, [fetchCourses]);

  // 7. Handle enroll một khóa học
  const handleEnroll = async (courseId) => {
    try {
      await axios.post('/api/student/courses/enroll', {
        studentId,
        courseId
      });
      // Sau khi enroll thành công, refetch để cập nhật
      fetchCourses();
    } catch (err) {
      console.error('Error enrolling course:', err);
      alert('Failed to enroll in course.');
    }
  };

  // 8. Handle sort change
  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Nút Back to Subjects */}
      <button
        onClick={() => navigate.push('/student/subjects')}
        className="flex items-center text-gray-600 hover:text-gray-900 mb-6"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Subjects</span>
      </button>

      {/* Thanh Search + Sort */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search courses..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Tiêu đề Subject */}
      <h2 className="text-2xl font-semibold text-gray-700 mb-4">
        Subject Courses
      </h2>

      {/* Danh sách Course */}
      {isLoading ? (
        <div className="text-gray-500">Loading courses...</div>
      ) : courses.length === 0 ? (
        <div className="text-gray-500">No courses found for this subject.</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {courses.map((course) => (
            <motion.div
              key={course._id}
              className={`p-4 rounded-lg shadow flex flex-col justify-between ${
                course.enrolled
                  ? 'bg-green-100 border-2 border-green-300'
                  : 'bg-white border border-gray-200'
              }`}
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <div>
                <h3 className="text-lg font-medium text-gray-800">
                  {course.title}
                </h3>
                <p className="text-sm text-gray-600 mt-1">
                  Credits: {course.credits || 'N/A'}
                </p>
                {course.startDate && (
                  <p className="text-sm text-gray-600">
                    Start: {new Date(course.startDate).toLocaleDateString()}
                  </p>
                )}
                {course.endDate && (
                  <p className="text-sm text-gray-600">
                    End: {new Date(course.endDate).toLocaleDateString()}
                  </p>
                )}
              </div>
              <div className="mt-4 flex justify-between items-center">
                {course.enrolled ? (
                  <button
                    onClick={() => {
                      // Chuyển sang trang detail course
                      navigate.push(`/student/course/${course._id}`);
                    }}
                    className="bg-green-600 text-white rounded-md px-3 py-1 text-sm hover:bg-green-700"
                  >
                    Detail
                  </button>
                ) : (
                  <button
                    onClick={() => handleEnroll(course._id)}
                    className="bg-blue-600 text-white rounded-md px-3 py-1 text-sm hover:bg-blue-700"
                  >
                    Enroll
                  </button>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}
