import React, { useState, useEffect, useCallback } from 'react';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Form,
  InputGroup,
  Spinner
} from 'react-bootstrap';
import { FiSearch, FiChevronDown } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';

const sortOptions = [
  { label: 'Created Date ↑', value: 'createdAt:asc' },
  { label: 'Created Date ↓', value: 'createdAt:desc' },
  { label: 'Name A→Z',       value: 'name:asc' },
  { label: 'Name Z→A',       value: 'name:desc' }
];

export default function StudentSubjects() {
  const navigate = useNavigate();
  const DEFAULT_ID = '60a000000000000000000002';

  const [studentId, setStudentId] = useState(DEFAULT_ID);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [order, setOrder] = useState('asc');
  const [enrolled, setEnrolled] = useState([]);
  const [others, setOthers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    try {
      const u = JSON.parse(localStorage.getItem('user') || '{}');
      if (u._id) setStudentId(u._id);
    } catch {}
  }, []);

  
  const fetchEnrolled = useCallback(async () => {
    const res = await fetch(`/api/student/subjects/by-student/${studentId}`);
    const body = await res.json();
    if (body.success) {
      setEnrolled(body.data);
      return body.data;
    }
    setEnrolled([]);
    return [];
  }, [studentId]);

  // fetchAll: nhận enrolledList rồi lọc others
  const fetchAll = useCallback(async (enrolledList) => {
    let url = `/api/student/subjects?sortBy=${sortBy}&order=${order}`;
    if (searchQuery.trim()) {
      url = `/api/student/subjects/search?q=${encodeURIComponent(
        searchQuery
      )}&sortBy=${sortBy}&order=${order}`;
    }
    const res = await fetch(url);
    const body = await res.json();
    if (body.success) {
      const all = body.data;
      const enrolledIds = new Set(enrolledList.map(s => s._id));
      setOthers(all.filter(s => !enrolledIds.has(s._id)));
    } else {
      setOthers([]);
    }
  }, [searchQuery, sortBy, order]);

 useEffect(() => {
  if (!studentId) return;
  const controller = new AbortController();
  const loadEnrolled = async () => {
    setLoading(true);
    try {
      const data = await fetchEnrolled({ signal: controller.signal });
      setEnrolled(data);
    } catch (err) {
      console.error('fetchEnrolled error', err);
    } finally {
      setLoading(false);
    }
  };
  loadEnrolled();
  return () => controller.abort();
}, [studentId, fetchEnrolled]);

// 2. fetchOthers: chạy mỗi khi enrolled hoặc filter thay đổi
useEffect(() => {
  if (!studentId) return;
  const controller = new AbortController();
  const loadOthers = async () => {
    setLoading(true);
    try {
      await fetchAll(enrolled, { signal: controller.signal });
    } catch (err) {
      console.error('fetchAll error', err);
    } finally {
      setLoading(false);
    }
  };
  loadOthers();
  return () => controller.abort();
}, [studentId, enrolled, searchQuery, sortBy, order, fetchAll]);

  const handleSort = e => {
    const [f, o] = e.target.value.split(':');
    setSortBy(f);
    setOrder(o);
  };

  const goCourses = (id, enrolledFlag) => {
    navigate(`/student/courses?subjectId=${id}&enrolled=${enrolledFlag}`);
  };

  return (
    <Container className="py-4">
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search subjects..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <Form.Select value={`${sortBy}:${order}`} onChange={handleSort}>
            {sortOptions.map(opt => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </Form.Select>
        </Col>
      </Row>

      <h4>My Enrolled Subjects</h4>
      {loading ? (
        <Spinner animation="border" />
      ) : enrolled.length === 0 ? (
        <p>You have not enrolled in any subject yet.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3 mb-4">
          {enrolled.map(sub => (
            <Col key={sub._id}>
              <Card className="h-100 border-primary">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{sub.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">
                    Code: {sub.code || '—'}
                  </Card.Subtitle>
                  <Button
                    variant="primary"
                    className="mt-auto"
                    onClick={() => goCourses(sub._id, true)}
                  >
                    View Courses
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}

      <h4>Other Subjects</h4>
      {loading ? (
        <Spinner animation="border" />
      ) : others.length === 0 ? (
        <p>No other subjects found.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {others.map(sub => (
            <Col key={sub._id}>
              <Card className="h-100 border-warning">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{sub.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">
                    Code: {sub.code || '—'}
                  </Card.Subtitle>
                  <Button
                    variant="warning"
                    className="mt-auto text-white"
                    onClick={() => goCourses(sub._id, false)}
                  >
                    View Courses
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
