import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup
} from 'react-bootstrap';

export default function CourseList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const subjectId = params.get('subjectId');

  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);
 // const [studentId] = useState(() => {
   // try {
   //   const userData = localStorage.getItem('user');
    //  console.log('localStorage user data:', userData); // Debug log
    //  if (userData) {
     //   const parsed = JSON.parse(userData);
      //  console.log('Parsed user:', parsed); // Debug log
      //  return parsed._id || DEFAULT_STUDENT_ID;
      //}
      //return DEFAULT_STUDENT_ID;
    //} catch (error) {
    //  console.error('Error parsing user from localStorage:', error);
     // return DEFAULT_STUDENT_ID;
   // }
//  });

  // Debug: Log studentId
  useEffect(() => {
    console.log('Current studentId:', studentId);
  }, [studentId]);

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!subjectId) return;
    
    loadCourses();
  }, [subjectId, studentId, searchQuery, sortBy, order]);

  const loadCourses = async () => {
    setIsLoading(true);
    
    try {
      const cancel = axios.CancelToken.source();
      
      // Always get courses with enrollment status
      let apiUrl = `/api/student/courses/subject/${subjectId}/student/${studentId}`;
      let params = {};
      
      // Add search and sort parameters
      if (searchQuery.trim()) {
        params.q = searchQuery.trim();
      }
      
      if (sortBy && order) {
        params.sortBy = sortBy;
        params.order = order;
      }
      
      const response = await axios.get(apiUrl, { 
        params,
        cancelToken: cancel.token 
      });
      
      if (response.data.success) {
        setCourses(response.data.data);
      } else {
        setCourses([]);
      }
    } catch (err) {
      if (!axios.isCancel(err)) {
        console.error('Error loading courses:', err);
        setCourses([]);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleEnroll = async (id) => {
    try {
      console.log('Enrolling studentId:', studentId, 'courseId:', id); // Debug log
      
      const { data } = await axios.post(
        '/api/student/enrollments/enroll',
        { studentId, courseId: id }
      );
      
      console.log('Enroll response:', data); // Debug log
      
      if (data.success) {
        // Update local state immediately
        setCourses(prev =>
          prev.map(c => c._id === id ? { ...c, enrolled: true } : c)
        );
        console.log('Enrollment successful, updated local state');
      } else {
        console.error('Enrollment failed:', data.message);
        alert('Enrollment failed: ' + (data.message || 'Unknown error'));
      }
    } catch (err) {
      console.error('Enroll error:', err);
      alert('Error enrolling: ' + (err.response?.data?.message || err.message));
    }
  };

  const onSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <Container className="py-4">
      <Button variant="link" onClick={() => navigate('/student/subjects')} className="mb-4 p-0">
        <FiArrowLeft /> Back to Subjects
      </Button>

      {/* Search + Sort */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search courses..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <InputGroup>
            <Form.Select value={`${sortBy}:${order}`} onChange={e => onSortChange(e.target.value)}>
              <option value="title:asc">Title A→Z</option>
              <option value="title:desc">Title Z→A</option>
              <option value="startDate:asc">Start Date ↑</option>
              <option value="startDate:desc">Start Date ↓</option>
              <option value="credits:asc">Credits ↑</option>
              <option value="credits:desc">Credits ↓</option>
            </Form.Select>
            <InputGroup.Text><FiChevronDown /></InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      <h4>Subject Courses</h4>
      {isLoading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
        </div>
      ) : courses.length === 0 ? (
        <p>No courses found.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {courses.map(c => (
            <Col key={c._id}>
              <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.2 }}>
                <Card border={c.enrolled ? 'success' : 'secondary'} className="h-100">
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{c.title}</Card.Title>
                    <Card.Text className="text-muted mb-2">
                      Credits: {c.credits || 'N/A'}
                    </Card.Text>
                    <Card.Text className="text-muted mb-2">
                      Description: {c.description || 'N/A'}
                    </Card.Text>
                    {c.startDate && (
                      <Card.Text className="text-muted small">
                        Start: {new Date(c.startDate).toLocaleDateString()}
                      </Card.Text>
                    )}
                    <div className="mt-auto d-flex justify-content-between">
                      {c.enrolled ? (
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => navigate(`/student/course/${c._id}`)}
                        >
                          Detail
                        </Button>
                      ) : (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleEnroll(c._id)}
                        >
                          Enroll
                        </Button>
                      )}
                    </div>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
