// src/components/CourseDetail.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const sortOptions = [
  { label: 'Title A→Z', value: 'title:asc' },
  { label: 'Title Z→A', value: 'title:desc' }
];

export default function CourseDetail() {
  const navigate = useNavigate();
  const { courseId } = useParams(); // Giả sử route: /student/course/:courseId

  // 1. State để lưu course detail
  const [course, setCourse] = useState(null);

  // 2. State cho module list, search + sort
  const [modules, setModules] = useState([]);
  const [filteredModules, setFilteredModules] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');

  const [isLoading, setIsLoading] = useState(true);

  // 3. Fetch course detail
  const fetchCourseDetail = useCallback(async () => {
    try {
      setIsLoading(true);
      const resp = await axios.get(`/api/student/courses/${courseId}`);
      if (resp.data.success) {
        const data = resp.data.data;
        setCourse(data);
        setModules(data.modules || []);
        setFilteredModules(data.modules || []);
      }
    } catch (err) {
      console.error('Error fetching course detail:', err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId]);

  useEffect(() => {
    fetchCourseDetail();
  }, [fetchCourseDetail]);

  // 4. Handler khi search hoặc sort thay đổi
  useEffect(() => {
    let temp = [...modules];

    // 4.1. Filter theo searchQuery (search module title)
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, 'i');
      temp = temp.filter((m) => regex.test(m.title));
    }

    // 4.2. Sắp xếp theo sortBy & order
    temp.sort((a, b) => {
      const fieldA = a[sortBy] ? a[sortBy].toString().toLowerCase() : '';
      const fieldB = b[sortBy] ? b[sortBy].toString().toLowerCase() : '';
      if (fieldA < fieldB) return order === 'asc' ? -1 : 1;
      if (fieldA > fieldB) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFilteredModules(temp);
  }, [modules, searchQuery, sortBy, order]);

  // 5. Handler sort change
  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  if (isLoading) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <div className="text-gray-600">Loading course details...</div>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <div className="text-red-500">Course not found.</div>
        <button
          onClick={() => history.goBack()}
          className="mt-4 bg-green-600 text-white rounded-md px-4 py-2 hover:bg-green-700"
        >
          Back
        </button>
      </div>
    );
  }

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Nút Back to Course List */}
      <button
        onClick={() => history.goBack()}
        className="flex items-center text-white font-medium bg-green-600 hover:bg-green-700 rounded-md px-4 py-2 mb-6"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Courses</span>
      </button>

      {/* Header Course */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Course name: {course.title}
        </h1>
        <p className="text-gray-800 mt-1">
          Instructor: {course.instructorName || 'Unknown'}
        </p>
      </div>

      {/* Search + Sort Modules */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search Module */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search modules..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort Module */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Danh sách Module */}
      <div className="space-y-4 mb-8">
        {filteredModules.length === 0 ? (
          <div className="text-gray-600">No modules found.</div>
        ) : (
          filteredModules.map((mod) => (
            <motion.div
              key={mod.moduleId}
              className="bg-orange-400 rounded-md p-4 flex flex-col shadow"
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <h3 className="text-lg font-medium text-white">{mod.title}</h3>
              {mod.description && (
                <p className="text-white text-sm mt-1">{mod.description}</p>
              )}
            </motion.div>
          ))
        )}
      </div>

      {/* Nút Grade Overview & Assignment */}
      <div className="flex space-x-4">
        <button
          onClick={() => history.push(`/student/grades?courseId=${course._id}`)}
          className="bg-blue-600 text-white rounded-md px-4 py-2 hover:bg-blue-700"
        >
          Grade Overview
        </button>
        <button
          onClick={() => history.push(`/student/assignments?courseId=${course._id}`)}
          className="bg-gray-400 text-white rounded-md px-4 py-2 hover:bg-gray-500"
        >
          Assignments
        </button>
      </div>
    </div>
  );
}
