import React, { useState, useEffect, useCallback } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import { FiArrowLeft, FiSearch, FiChevronDown } from "react-icons/fi";
import { motion } from "framer-motion";

const sortOptions = [
  { label: "Newest First",    value: "appealCreatedAt:desc" },
  { label: "Oldest First",    value: "appealCreatedAt:asc" },
  { label: "Subject A→Z",     value: "subjectName:asc" },
  { label: "Subject Z→A",     value: "subjectName:desc" },
  { label: "Course A→Z",      value: "courseTitle:asc" },
  { label: "Course Z→A",      value: "courseTitle:desc" }
];

export default function AppealList() {
  const history = useHistory();

  // Lấy studentId từ localStorage hoặc mặc định
  const DEFAULT_STUDENT_ID = "60a000000000000000000002";
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);
  useEffect(() => {
    try {
      const stored = localStorage.getItem("user");
      if (stored) {
        const u = JSON.parse(stored);
        if (u && u._id) setStudentId(u._id);
      }
    } catch (e) {
      console.warn("Error parsing user from localStorage:", e);
    }
  }, []);

  // State
  const [appeals, setAppeals] = useState([]);   // Gốc
  const [filtered, setFiltered] = useState([]); // Sau filter+sort
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("appealCreatedAt");
  const [order, setOrder] = useState("desc");
  const [isLoading, setIsLoading] = useState(true);

  // 1. Fetch appeals của student
  const fetchAppeals = useCallback(async () => {
    try {
      setIsLoading(true);
      const resp = await axios.get(`/api/student/appeals?studentId=${studentId}`);
      if (resp.data.success) {
        setAppeals(resp.data.data);
        setFiltered(resp.data.data);
      }
    } catch (err) {
      console.error("Error fetching appeals:", err);
    } finally {
      setIsLoading(false);
    }
  }, [studentId]);

  useEffect(() => {
    fetchAppeals();
  }, [fetchAppeals]);

  // 2. Filter + Sort
  useEffect(() => {
    let temp = [...appeals];

    // 2.1. Filter theo searchQuery: tìm trong subjectName || courseTitle
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, "i");
      temp = temp.filter(
        (a) =>
          regex.test(a.subjectName) || regex.test(a.courseTitle)
      );
    }

    // 2.2. Sort
    temp.sort((a, b) => {
      let fieldA, fieldB;

      switch (sortBy) {
        case "appealCreatedAt":
          fieldA = new Date(a.appealCreatedAt).getTime();
          fieldB = new Date(b.appealCreatedAt).getTime();
          break;
        case "subjectName":
          fieldA = a.subjectName?.toLowerCase() || "";
          fieldB = b.subjectName?.toLowerCase() || "";
          break;
        case "courseTitle":
          fieldA = a.courseTitle?.toLowerCase() || "";
          fieldB = b.courseTitle?.toLowerCase() || "";
          break;
        default:
          fieldA = new Date(a.appealCreatedAt).getTime();
          fieldB = new Date(b.appealCreatedAt).getTime();
      }

      if (fieldA < fieldB) return order === "asc" ? -1 : 1;
      if (fieldA > fieldB) return order === "asc" ? 1 : -1;
      return 0;
    });

    setFiltered(temp);
  }, [appeals, searchQuery, sortBy, order]);

  // 3. Handle sort change
  const handleSortChange = (value) => {
    const [field, ord] = value.split(":");
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back Button */}
      <button
        onClick={() => history.goBack()}
        className="flex items-center bg-green-600 text-white rounded-md px-4 py-2 mb-6 hover:bg-green-700"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Dashboard</span>
      </button>

      {/* Header */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          My Appeals
        </h1>
      </div>

      {/* Search + Sort */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search Input */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search subject or course..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort Dropdown */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Appeal List */}
      {isLoading ? (
        <div className="text-gray-600">Loading appeals...</div>
      ) : filtered.length === 0 ? (
        <div className="text-gray-600">No appeals found.</div>
      ) : (
        <div className="space-y-4">
          {filtered.map((a) => (
            <motion.div
              key={a.appealId}
              className="bg-white border border-gray-200 rounded-md p-4 shadow"
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <div className="flex justify-between mb-2">
                <div>
                  <h3 className="text-lg font-semibold text-gray-800">
                    Subject: {a.subjectName}
                  </h3>
                  <p className="text-gray-700">Course: {a.courseTitle}</p>
                </div>
                <span className="text-sm text-gray-500">
                  {new Date(a.appealCreatedAt).toLocaleDateString()}
                </span>
              </div>
              <div className="mb-2">
                <p className="text-gray-800">
                  <span className="font-medium">Content:</span>{" "}
                  {a.appealComments.length
                    ? a.appealComments[a.appealComments.length - 1].text
                    : "(No content)"}
                </p>
              </div>
              <div className="flex justify-between">
                <p className="text-gray-700">
                  Status:{" "}
                  <span
                    className={
                      a.appealStatus === "open"
                        ? "text-blue-600"
                        : "text-green-600"
                    }
                  >
                    {a.appealStatus}
                  </span>
                </p>
                <p className="text-gray-700">
                  Grade:{" "}
                  {a.gradeScore != null ? (
                    <span className="text-green-700">{a.gradeScore}</span>
                  ) : (
                    <span className="text-red-600">Not graded</span>
                  )}
                </p>
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}
