// src/components/CourseList.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Spinner,
  Form,
  InputGroup
} from 'react-bootstrap';
const sortOptions = [
  { label: 'Title A→Z', value: 'title:asc' },
  { label: 'Title Z→A', value: 'title:desc' },
  { label: 'Start Date ↑', value: 'startDate:asc' },
  { label: 'Start Date ↓', value: 'startDate:desc' },
  { label: 'Credits ↑', value: 'credits:asc' },
  { label: 'Credits ↓', value: 'credits:desc' }
];

export default function CourseList() {
  const navigate = useNavigate();
  const location = useLocation();

  //  Lấy subjectId từ query param: ?subjectId=xxx
  const params = new URLSearchParams(location.search);
  const subjectId = params.get('subjectId');
  const enrolledParam = params.get('enrolled') === 'true';

  // Lấy studentId từ localStorage, nếu không tồn tại dùng mặc định
  const DEFAULT_STUDENT_ID = '60a000000000000000000002';
  const [studentId, setStudentId] = useState(DEFAULT_STUDENT_ID);

  //  State local
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('title');
  const [order, setOrder] = useState('asc');
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const fetchCourses = useCallback(async () => {
  if (!subjectId || !studentId) return;
  setIsLoading(true);

  try {
    let baseUrl = `/api/student/courses/subject/${subjectId}/student/${studentId}`;
    let courses = [];

    // Step 1: Fetch base courses for subjectId and studentId
    const baseResp = await fetch(baseUrl);
    const baseData = await baseResp.json();

    if (baseData.success) {
      courses = baseData.data;
    }

    // Step 2: Apply search filter if needed (client-side or make a separate fetch to /search)
    if (searchQuery.trim()) {
      // If backend supports search by subject, use this endpoint:
      const searchResp = await fetch(`/api/student/courses/search?q=${encodeURIComponent(searchQuery)}`);
      const searchData = await searchResp.json();

      if (searchData.success) {
        // Filter results to keep only those matching subjectId and studentId
        const regex = new RegExp(searchQuery, 'i');
        courses = searchData.data.filter(
          (course) =>
            course.subjectId === subjectId &&
            regex.test(course.title || '') ||
            regex.test(course.description || '')
        );
      }
    }

    // Step 3: Sort if needed
    if (sortBy) {
      const sortResp = await fetch(`/api/student/courses/sort?sortBy=${sortBy}&order=${order}`);
      const sortData = await sortResp.json();

      if (sortData.success) {
        const sortedIds = sortData.data.map(c => c._id);
        courses.sort((a, b) => sortedIds.indexOf(a._id) - sortedIds.indexOf(b._id));
      }
    }

    setCourses(courses);
  } catch (err) {
    console.error('Error fetching courses:', err);
  } finally {
    setIsLoading(false);
  }
}, [subjectId, studentId, searchQuery, sortBy, order]);

useEffect(() => {
  fetchCourses();
}, [fetchCourses]);

const handleEnroll = async (courseId) => {
  try {
    const response = await fetch('/api/student/courses/enroll', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ studentId, courseId }),
    });

    const data = await response.json();

    if (data.success) {
      fetchCourses();
    } else {
      alert('Enrollment failed.');
    }
  } catch (err) {
    console.error('Error enrolling course:', err);
    alert('Failed to enroll in course.');
  }
};

  // 8. Handle sort change
  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  return (
    <Container className="py-4">
      {/* Back to Subjects */}
      <Button variant="link" className="mb-4 p-0" onClick={() => navigate('/student/subjects')}>
        <FiArrowLeft /> Back to Subjects
      </Button>

      {/* Search + Sort */}
      <Row className="align-items-center mb-3">
        <Col md={8}>
          <InputGroup>
            <InputGroup.Text><FiSearch /></InputGroup.Text>
            <Form.Control
              placeholder="Search courses..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
            />
          </InputGroup>
        </Col>
        <Col md={4}>
          <InputGroup>
            <Form.Select
              value={`${sortBy}:${order}`}
              onChange={e => handleSortChange(e.target.value)}
            >
              {sortOptions.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </Form.Select>
            <InputGroup.Text><FiChevronDown /></InputGroup.Text>
          </InputGroup>
        </Col>
      </Row>

      {/* Title */}
      <h4 className="mb-3">Subject Courses</h4>

      {/* Course List */}
      {isLoading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
        </div>
      ) : courses.length === 0 ? (
        <p>No courses found for this subject.</p>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-3">
          {courses.map(course => (
            <Col key={course._id}>
              <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.2 }}>
                <Card
                  border={course.enrolled ? 'success' : 'secondary'}
                  className="h-100"
                >
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{course.title}</Card.Title>
                    <Card.Text className="text-muted mb-2">
                      Credits: {course.credits ?? 'N/A'}
                    </Card.Text>
                    {course.startDate && (
                      <Card.Text className="text-muted small">
                        Start: {new Date(course.startDate).toLocaleDateString()}
                      </Card.Text>
                    )}
                    {course.endDate && (
                      <Card.Text className="text-muted small">
                        End: {new Date(course.endDate).toLocaleDateString()}
                      </Card.Text>
                    )}
                    <div className="mt-auto d-flex justify-content-between">
                      {course.enrolled ? (
                        <Button
                          variant="success"
                          size="sm"
                          onClick={() => navigate(`/student/course/${course._id}`)}
                        >
                          Detail
                        </Button>
                      ) : (
                        <Button
                          variant="primary"
                          size="sm"
                          onClick={() => handleEnroll(course._id)}
                        >
                          Enroll
                        </Button>
                      )}
                    </div>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
