// src/components/AssignmentList.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FiSearch, FiChevronDown, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

const sortOptions = [
  { label: 'Due Date ↑',  value: 'dueDate:asc' },
  { label: 'Due Date ↓',  value: 'dueDate:desc' },
  { label: 'Title A→Z',   value: 'title:asc' },
  { label: 'Title Z→A',   value: 'title:desc' },
  { label: 'Created ↑',   value: 'createdAt:asc' },
  { label: 'Created ↓',   value: 'createdAt:desc' }
];

export default function AssignmentList() {
  const history = useHistory(); // Đối với React Router v5; với v6 dùng useNavigate()
  const { courseId } = useParams(); // Giả sử route: /student/assignments/:courseId

  // 1. State quản lý
  const [course, setCourse] = useState(null);       // Course detail
  const [assignments, setAssignments] = useState([]); // Mảng gốc
  const [filtered, setFiltered] = useState([]);     // Mảng sau filter/sort
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('dueDate');
  const [order, setOrder] = useState('asc');
  const [isLoading, setIsLoading] = useState(true);

  // 2. Fetch Course detail
  const fetchCourseDetail = useCallback(async () => {
    try {
      const resp = await axios.get(`/api/student/courses/${courseId}`);
      if (resp.data.success) {
        setCourse(resp.data.data);
      }
    } catch (err) {
      console.error('Error fetching course detail:', err);
    }
  }, [courseId]);

  // 3. Fetch Assignments cho course
  const fetchAssignments = useCallback(async () => {
    try {
      setIsLoading(true);
      const resp = await axios.get(`/api/student/assignments/course/${courseId}`);
      if (resp.data.success) {
        setAssignments(resp.data.data);
        setFiltered(resp.data.data);
      }
    } catch (err) {
      console.error('Error fetching assignments:', err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId]);

  // 4. Khi component mount, fetch cả course và assignments
  useEffect(() => {
    fetchCourseDetail();
    fetchAssignments();
  }, [fetchCourseDetail, fetchAssignments]);

  // 5. Lọc + Sắp xếp Assignments
  useEffect(() => {
    let temp = [...assignments];

    // 5.1. Filter theo searchQuery (tiêu đề)
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, 'i');
      temp = temp.filter((a) => regex.test(a.title));
    }

    // 5.2. Sort theo sortBy & order
    temp.sort((a, b) => {
      let fieldA = a[sortBy];
      let fieldB = b[sortBy];
      // Nếu là Date, chuyển thành timestamp
      if (sortBy === 'dueDate' || sortBy === 'createdAt') {
        fieldA = new Date(fieldA).getTime();
        fieldB = new Date(fieldB).getTime();
      } else {
        // Chuyển về lowercase để so sánh string
        fieldA = fieldA ? fieldA.toString().toLowerCase() : '';
        fieldB = fieldB ? fieldB.toString().toLowerCase() : '';
      }
      if (fieldA < fieldB) return order === 'asc' ? -1 : 1;
      if (fieldA > fieldB) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFiltered(temp);
  }, [assignments, searchQuery, sortBy, order]);

  // 6. Handler khi chọn sort option
  const handleSortChange = (value) => {
    const [field, ord] = value.split(':');
    setSortBy(field);
    setOrder(ord);
  };

  // 7. Handler khi click xem quiz của assignment
  const goToQuizList = (assignmentId) => {
    history.push(`/student/quiz/${assignmentId}`);
  };

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back Button */}
      <button
        onClick={() => history.goBack()}
        className="flex items-center text-white bg-green-600 hover:bg-green-700 rounded-md px-4 py-2 mb-6"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Courses</span>
      </button>

      {/* Header Course */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Assignments for Course: {course ? course.title : courseId}
        </h1>
        {course && course.instructorName && (
          <p className="text-gray-800 mt-1">
            Instructor: {course.instructorName}
          </p>
        )}
      </div>

      {/* Search + Sort */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search Input */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search assignments..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort Dropdown */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Danh sách Assignment */}
      {isLoading ? (
        <div className="text-gray-600">Loading assignments...</div>
      ) : filtered.length === 0 ? (
        <div className="text-gray-600">No assignments found.</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filtered.map((a) => (
            <motion.div
              key={a._id}
              className="bg-red-200 rounded-md p-4 flex flex-col justify-between shadow"
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <div>
                <h3 className="text-lg font-semibold text-gray-900">
                  {a.title}
                </h3>
                {a.description && (
                  <p className="text-gray-700 text-sm mt-1">
                    {a.description.length > 60
                      ? a.description.slice(0, 60) + '...'
                      : a.description}
                  </p>
                )}
                <p className="text-gray-700 text-sm mt-2">
                  Due: {new Date(a.dueDate).toLocaleDateString()}
                </p>
              </div>
              <button
                onClick={() => goToQuizList(a._id)}
                className="mt-4 bg-blue-600 text-white rounded-md px-3 py-1 text-sm hover:bg-blue-700"
              >
                View Quiz
              </button>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}
