import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { FiArrowLeft, FiSearch, FiChevronDown } from "react-icons/fi";
import { motion } from "framer-motion";

const sortOptions = [
  { label: "Title A→Z", value: "title:asc" },
  { label: "Title Z→A", value: "title:desc" }
];

export default function ModuleDetail() {
  const history = useNavigate();
  const { courseId, moduleId } = useParams(); 
  // Route assumed: /student/course/:courseId/module/:moduleId

  // State
  const [courseTitle, setCourseTitle] = useState("");
  const [module, setModule] = useState(null); // { moduleId, title, lessons: [] }
  const [lessons, setLessons] = useState([]); // original lessons
  const [filtered, setFiltered] = useState([]); // filtered + sorted
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("title");
  const [order, setOrder] = useState("asc");
  const [isLoading, setIsLoading] = useState(true);

  // Fetch course & module details
  const fetchModule = useCallback(async () => {
    try {
      setIsLoading(true);
      // GET course detail
      const courseResp = await axios.get(`/api/student/courses/${courseId}`);
      if (courseResp.data.success) {
        setCourseTitle(courseResp.data.data.title);
        // Find module by moduleId
        const mod = courseResp.data.data.modules.find(
          (m) => m.moduleId === moduleId
        );
        if (mod) {
          setModule(mod);
          setLessons(mod.lessons || []);
          setFiltered(mod.lessons || []);
        } else {
          setModule(null);
          setLessons([]);
          setFiltered([]);
        }
      }
    } catch (err) {
      console.error("Error fetching course/module:", err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId, moduleId]);

  useEffect(() => {
    fetchModule();
  }, [fetchModule]);

  // Filter + Sort lessons
  useEffect(() => {
    let temp = [...lessons];

    // Search by lesson title
    if (searchQuery.trim()) {
      const regex = new RegExp(searchQuery, "i");
      temp = temp.filter((l) => regex.test(l.title));
    }

    // Sort
    temp.sort((a, b) => {
      const ta = a[sortBy]?.toLowerCase() || "";
      const tb = b[sortBy]?.toLowerCase() || "";
      if (ta < tb) return order === "asc" ? -1 : 1;
      if (ta > tb) return order === "asc" ? 1 : -1;
      return 0;
    });

    setFiltered(temp);
  }, [lessons, searchQuery, sortBy, order]);

  const handleSortChange = (value) => {
    const [field, ord] = value.split(":");
    setSortBy(field);
    setOrder(ord);
  };

  if (isLoading) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <div className="text-gray-600">Loading module...</div>
      </div>
    );
  }

  if (!module) {
    return (
      <div className="p-6 bg-yellow-50 min-h-screen">
        <p className="text-red-600">Module not found.</p>
        <button
          onClick={() => history.push(`/student/course/${courseId}`)}
          className="mt-4 bg-green-600 text-white rounded-md px-4 py-2 hover:bg-green-700"
        >
          Back to Course
        </button>
      </div>
    );
  }

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      {/* Back Button */}
      <button
        onClick={() => history.push(`/student/course/${courseId}`)}
        className="flex items-center bg-green-600 text-white rounded-md px-4 py-2 mb-6 hover:bg-green-700"
      >
        <FiArrowLeft size={20} />
        <span className="ml-2">Back to Course Detail</span>
      </button>

      {/* Header */}
      <div className="bg-blue-200 rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Course: {courseTitle}
        </h1>
        <p className="text-gray-800 mt-1">
          Module: {module.title}
        </p>
      </div>

      {/* Search + Sort Lessons */}
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 space-y-4 md:space-y-0">
        {/* Search Input */}
        <div className="flex items-center bg-white shadow rounded-md overflow-hidden w-full md:w-2/3">
          <FiSearch className="text-gray-400 ml-3" size={20} />
          <input
            type="text"
            placeholder="Search lessons..."
            className="flex-1 px-4 py-2 focus:outline-none"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {/* Sort Dropdown */}
        <div className="relative w-full md:w-1/3">
          <select
            className="w-full appearance-none bg-white border border-gray-300 rounded-md px-4 py-2 pr-8 focus:outline-none shadow"
            value={`${sortBy}:${order}`}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            {sortOptions.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
          <FiChevronDown className="pointer-events-none absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>

      {/* Lesson List */}
      {filtered.length === 0 ? (
        <div className="text-gray-600">No lessons found.</div>
      ) : (
        <div className="space-y-4">
          {filtered.map((lesson) => (
            <motion.div
              key={lesson.lessonId}
              className="bg-orange-100 rounded-md p-4 shadow"
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.2 }}
            >
              <h3 className="text-lg font-semibold text-gray-800">
                {lesson.title}
              </h3>
              {lesson.content && (
                <p className="text-gray-700 mt-2">
                  {lesson.content}
                </p>
              )}
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
}
